import React, { useState, useEffect } from 'react';
import './SignUpScreen.css';

function SignUpScreen({ onBackToLogin, onSignUpComplete }) {
    const [userName, setUserName] = useState('');
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');
    const [height, setHeight] = useState('');
    const [weight, setWeight] = useState('');
    const [birthdate, setBirthdate] = useState('');
    const [showDatePicker, setShowDatePicker] = useState(false);
    const [selectedDay, setSelectedDay] = useState('');
    const [selectedMonth, setSelectedMonth] = useState('');
    const [selectedYear, setSelectedYear] = useState('');
    const [errors, setErrors] = useState({});
    const [isCheckingUsername, setIsCheckingUsername] = useState(false);
    const [usernameAvailable, setUsernameAvailable] = useState(null);

    const days = Array.from({length: 31}, (_, i) => i + 1);

    const months = [
        '◊ô◊†◊ï◊ê◊®', '◊§◊ë◊®◊ï◊ê◊®', '◊û◊®◊•', '◊ê◊§◊®◊ô◊ú', '◊û◊ê◊ô', '◊ô◊ï◊†◊ô',
        '◊ô◊ï◊ú◊ô', '◊ê◊ï◊í◊ï◊°◊ò', '◊°◊§◊ò◊û◊ë◊®', '◊ê◊ï◊ß◊ò◊ï◊ë◊®', '◊†◊ï◊ë◊û◊ë◊®', '◊ì◊¶◊û◊ë◊®'
    ];

    const currentYear = new Date().getFullYear();
    const years = Array.from({length: 91}, (_, i) => currentYear - 10 - i);

    useEffect(() => {
        document.body.style.overflow = 'hidden';
        return () => {
          document.body.style.overflow = 'unset';
        };
    }, []);

    const validateField = (fieldName, value) => {
        let error = '';

        switch (fieldName) {
            case 'userName':
                if (!value.trim()) {
                    error = '◊©◊ù ◊û◊©◊™◊û◊© ◊î◊ï◊ê ◊©◊ì◊î ◊ó◊ï◊ë◊î';
                } else if (value.length < 3) {
                    error = '◊©◊ù ◊û◊©◊™◊û◊© ◊ó◊ô◊ô◊ë ◊ú◊î◊õ◊ô◊ú ◊ú◊§◊ó◊ï◊™ 3 ◊™◊ï◊ï◊ô◊ù';
                } else if (!/^[a-zA-Z0-9\s]+$/.test(value)) {
                    error = '◊©◊ù ◊û◊©◊™◊û◊© ◊ô◊õ◊ï◊ú ◊ú◊î◊õ◊ô◊ú ◊®◊ß ◊ê◊ï◊™◊ô◊ï◊™ ◊ë◊ê◊†◊í◊ú◊ô◊™, ◊û◊°◊§◊®◊ô◊ù ◊ï◊®◊ï◊ï◊ó◊ô◊ù';
                }
                break;

            case 'password':
                if (!value) {
                    error = '◊°◊ô◊°◊û◊î ◊î◊ô◊ê ◊©◊ì◊î ◊ó◊ï◊ë◊î';
                } else if (value.length < 6) {
                    error = '◊°◊ô◊°◊û◊î ◊ó◊ô◊ô◊ë◊™ ◊ú◊î◊õ◊ô◊ú ◊ú◊§◊ó◊ï◊™ 6 ◊™◊ï◊ï◊ô◊ù';
                }
                 else if (!/^[a-zA-Z0-9\s]+$/.test(value)) {
                error = '◊©◊ù ◊û◊©◊™◊û◊© ◊ô◊õ◊ï◊ú ◊ú◊î◊õ◊ô◊ú ◊®◊ß ◊ê◊ï◊™◊ô◊ï◊™ ◊ë◊ê◊†◊í◊ú◊ô◊™, ◊û◊°◊§◊®◊ô◊ù ◊ï◊®◊ï◊ï◊ó◊ô◊ù';
                }
                break;

            case 'email':
                if (!value) {
                    error = '◊ê◊ô◊û◊ô◊ô◊ú ◊î◊ï◊ê ◊©◊ì◊î ◊ó◊ï◊ë◊î';
                } else if (!value.includes('@') || !value.includes('.')) {
                    error = '◊ê◊†◊ê ◊î◊ñ◊ü ◊õ◊™◊ï◊ë◊™ ◊ê◊ô◊û◊ô◊ô◊ú ◊™◊ß◊ô◊†◊î';
                }

                break;

            case 'height':
                if (!value) {
                    error = '◊í◊ï◊ë◊î ◊î◊ï◊ê ◊©◊ì◊î ◊ó◊ï◊ë◊î';
                } else if (isNaN(value) || value < 100 || value > 250) {
                    error = '◊í◊ï◊ë◊î ◊ó◊ô◊ô◊ë ◊ú◊î◊ô◊ï◊™ ◊ë◊ô◊ü 100 ◊ú-250 ◊°"◊û';
                }
                break;

            case 'weight':
                if (!value) {
                    error = '◊û◊©◊ß◊ú ◊î◊ï◊ê ◊©◊ì◊î ◊ó◊ï◊ë◊î';
                } else if (isNaN(value) || value < 30 || value > 300) {
                    error = '◊û◊©◊ß◊ú ◊ó◊ô◊ô◊ë ◊ú◊î◊ô◊ï◊™ ◊ë◊ô◊ü 30 ◊ú-300 ◊ß"◊í';
                }
                break;

            case 'birthdate':
                if (!value) {
                    error = '◊™◊ê◊®◊ô◊ö ◊ú◊ô◊ì◊î ◊î◊ï◊ê ◊©◊ì◊î ◊ó◊ï◊ë◊î';
                } else if (!/^\d{2}\/\d{2}\/\d{4}$/.test(value)) {
                    error = '◊ê◊†◊ê ◊î◊ñ◊ü ◊™◊ê◊®◊ô◊ö ◊ë◊§◊ï◊®◊û◊ò DD/MM/YYYY';
                }
                break;

            default:
                break;
        }

        return error;
    };

    const checkUsernameAvailability = async (username) => {
        if (!username || username.length < 3) {
            setUsernameAvailable(null);
            return;
        }

        setIsCheckingUsername(true);
        try {
            const response = await fetch('https://wolfit-gym-backend-ijvq.onrender.com/api/check-username', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ userName: username })
            });

            const result = await response.json();
            setUsernameAvailable(result.available);

            if (!result.available) {
                setErrors(prev => ({
                    ...prev,
                    userName: '◊©◊ù ◊û◊©◊™◊û◊© ◊ñ◊î ◊õ◊ë◊® ◊™◊§◊ï◊°'
                }));
            } else {
                setErrors(prev => ({
                    ...prev,
                    userName: ''
                }));
            }
        } catch (error) {
            console.error('◊©◊í◊ô◊ê◊î ◊ë◊ë◊ì◊ô◊ß◊™ ◊©◊ù ◊û◊©◊™◊û◊©:', error);
            setUsernameAvailable(null);
        } finally {
            setIsCheckingUsername(false);
        }
    };

    const updateField = (fieldName, value, setter) => {
        setter(value);

        if (!value.trim()) {
            setErrors(prev => ({
                ...prev,
                [fieldName]: ''
            }));
            return;
        }

        const error = validateField(fieldName, value);
        console.log(`◊¢◊ì◊õ◊ï◊ü ◊©◊ì◊î ${fieldName}:`, { value, error });
        setErrors(prev => ({
            ...prev,
            [fieldName]: error
        }));

        // ◊ë◊ì◊ô◊ß◊™ ◊©◊ù ◊û◊©◊™◊û◊© ◊ë◊ñ◊û◊ü ◊ê◊û◊™
        if (fieldName === 'userName') {
            // ◊ë◊ì◊ô◊ß◊î ◊¢◊ù delay ◊õ◊ì◊ô ◊ú◊ê ◊ú◊ë◊ì◊ï◊ß ◊¢◊ú ◊õ◊ú ◊™◊ï
            setTimeout(() => {
                checkUsernameAvailability(value);
            }, 500);
        }
    };

    const isFormValid = () => {
        const allFieldsFilled = userName.trim() && password && email && height && weight && birthdate;
        const noErrors = Object.values(errors).every(error => !error);

        console.log('◊õ◊ú ◊î◊©◊ì◊ï◊™ ◊û◊ú◊ê◊ô◊ù:', allFieldsFilled);
        console.log('◊ê◊ô◊ü ◊©◊í◊ô◊ê◊ï◊™:', noErrors);
        console.log('◊¢◊®◊õ◊ô ◊î◊©◊ì◊ï◊™:', {
            userName: userName.trim(),
            password: password,
            email: email,
            height: height,
            weight: weight,
            birthdate: birthdate
        });
        console.log('◊©◊í◊ô◊ê◊ï◊™:', errors);

        return allFieldsFilled && noErrors;
    };

    const handleContinue = () => {
        if (!isFormValid()) {
            return;
        }

        console.log('◊†◊™◊ï◊†◊ô◊ù:', {
            userName,
            password, 
            email,
            height,
            weight,
            birthdate
        });

        if (onSignUpComplete) {
          const userData = {
            userName,
            password, 
            email,
            height,
            weight,
            birthdate
            };
            onSignUpComplete(userData);
        }
    };

    return (
        <div className="signup-container">
          <button className="back-button" onClick={onBackToLogin}>
            ◊ó◊ñ◊®◊î
          </button>

          <div className="content">
            <h1>◊î◊®◊©◊û◊î</h1>
            <p>◊î◊ñ◊ü ◊ê◊™ ◊§◊®◊ò◊ô ◊î◊û◊©◊™◊û◊© ◊©◊ú◊ö</p>

            <div className="signup-form">
              <div className="form-group">
                <div className="input-container">
                  <input 
                    type="text" 
                    placeholder="◊©◊ù ◊û◊©◊™◊û◊©"
                    value={userName}
                    onChange={(e) => updateField('userName', e.target.value, setUserName)}
                    onBlur={(e) => {
                      const error = validateField('userName', e.target.value);
                      setErrors(prev => ({
                        ...prev,
                        userName: error
                      }));
                    }}
                    className={errors.userName ? 'error' : ''}
                  />
                  {isCheckingUsername && (
                    <div className="username-checking">
                      <div className="spinner"></div>
                      ◊ë◊ï◊ì◊ß...
                    </div>
                  )}
                  {!isCheckingUsername && usernameAvailable === true && userName.length >= 3 && (
                    <div className="username-available">‚úì ◊ñ◊û◊ô◊ü</div>
                  )}
                  {!isCheckingUsername && usernameAvailable === false && (
                    <div className="username-taken">‚úó ◊™◊§◊ï◊°</div>
                  )}
                </div>
                {errors.userName && <div className="error-message">{errors.userName}</div>}
              </div>

              <div className="form-group">
                <input 
                  type="password" 
                  placeholder="◊°◊ô◊°◊û◊î"
                  value={password}
                  onChange={(e) => updateField('password', e.target.value, setPassword)}
                  onBlur={(e) => {
                    const error = validateField('password', e.target.value);
                    setErrors(prev => ({
                      ...prev,
                      password: error
                    }));
                  }}
                  className={errors.password ? 'error' : ''}
                />
                {errors.password && <div className="error-message">{errors.password}</div>}
              </div>

              <div className="form-group">
                <input 
                  type="email" 
                  placeholder="◊ê◊ô◊û◊ô◊ô◊ú"
                  value={email}
                  onChange={(e) => updateField('email', e.target.value, setEmail)}
                  onBlur={(e) => {
                    const error = validateField('email', e.target.value);
                    setErrors(prev => ({
                      ...prev,
                      email: error
                    }));
                  }}
                  className={errors.email ? 'error' : ''}
                />
                {errors.email && <div className="error-message">{errors.email}</div>}
              </div>

              <div className="form-group">
                <input 
                  type="number" 
                  placeholder="◊í◊ï◊ë◊î (◊°◊¥◊û)"
                  value={height}
                  onChange={(e) => updateField('height', e.target.value, setHeight)}
                  onBlur={(e) => {
                    const error = validateField('height', e.target.value);
                    setErrors(prev => ({
                      ...prev,
                      height: error
                    }));
                  }}
                  className={errors.height ? 'error' : ''}
                />
                {errors.height && <div className="error-message">{errors.height}</div>}
              </div>

              <div className="form-group">
                <input 
                  type="number" 
                  placeholder="◊û◊©◊ß◊ú (◊ß◊¥◊í)"
                  value={weight}
                  onChange={(e) => updateField('weight', e.target.value, setWeight)}
                  onBlur={(e) => {
                    const error = validateField('weight', e.target.value);
                    setErrors(prev => ({
                      ...prev,
                      weight: error
                    }));
                  }}
                  className={errors.weight ? 'error' : ''}
                />
                {errors.weight && <div className="error-message">{errors.weight}</div>}
              </div>

              <div className="form-group">
                <div className="date-input-container">
                  <input 
                    type="text" 
                    placeholder="◊™◊ê◊®◊ô◊ö ◊ú◊ô◊ì◊î (DD/MM/YYYY)"
                    value={birthdate}
                    onChange={(e) => {
                      let value = e.target.value;
                      value = value.replace(/[^\d/]/g, '');

                      if (value.length === 2 && !value.includes('/')) {
                        value = value + '/';
                      } else if (value.length === 5 && value.split('/').length === 2) {
                        value = value + '/';
                      }

                      // ◊î◊í◊ë◊ú ◊ú-10 ◊™◊ï◊ï◊ô◊ù (DD/MM/YYYY)
                      if (value.length <= 10) {
                        updateField('birthdate', value, setBirthdate);
                      }
                    }}
                    onBlur={(e) => {
                      const error = validateField('birthdate', e.target.value);
                      setErrors(prev => ({
                        ...prev,
                        birthdate: error
                      }));
                    }}
                    maxLength="10"
                    className={errors.birthdate ? 'error' : ''}
                  />
                  <button 
                    type="button" 
                    className="date-picker-toggle"
                    onClick={() => setShowDatePicker(!showDatePicker)}
                  >
                    üìÖ
                  </button>
                </div>
                {errors.birthdate && <div className="error-message">{errors.birthdate}</div>}

                {showDatePicker && (
                  <div className="date-picker-dropdown">
                    <div className="date-selectors">
                      <select 
                        value={selectedDay} 
                        onChange={(e) => {
                          setSelectedDay(e.target.value);
                          if (selectedMonth && selectedYear) {
                            const monthIndex = months.indexOf(selectedMonth) + 1;
                            const newDate = `${e.target.value}/${monthIndex.toString().padStart(2, '0')}/${selectedYear}`;
                            updateField('birthdate', newDate, setBirthdate);
                          }
                        }}
                      >
                        <option value="">◊ô◊ï◊ù</option>
                        {days.map(day => (
                          <option key={day} value={day.toString().padStart(2, '0')}>{day}</option>
                        ))}
                      </select>

                      <select 
                        value={selectedMonth} 
                        onChange={(e) => {
                          setSelectedMonth(e.target.value);
                          if (selectedDay && selectedYear) {
                            const monthIndex = months.indexOf(e.target.value) + 1;
                            const newDate = `${selectedDay}/${monthIndex.toString().padStart(2, '0')}/${selectedYear}`;
                            updateField('birthdate', newDate, setBirthdate);
                          }
                        }}
                      >
                        <option value="">◊ó◊ï◊ì◊©</option>
                        {months.map((month, index) => (
                          <option key={month} value={month}>{month}</option>
                        ))}
                      </select>

                      <select 
                        value={selectedYear} 
                        onChange={(e) => {
                          setSelectedYear(e.target.value);
                          if (selectedDay && selectedMonth) {
                            const monthIndex = months.indexOf(selectedMonth) + 1;
                            const newDate = `${selectedDay}/${monthIndex.toString().padStart(2, '0')}/${e.target.value}`;
                            updateField('birthdate', newDate, setBirthdate);
                          }
                        }}
                      >
                        <option value="">◊©◊†◊î</option>
                        {years.map(year => (
                          <option key={year} value={year}>{year}</option>
                        ))}
                      </select>
                    </div>
                  </div>
                )}
              </div>

              {(() => {
                const isValid = isFormValid();
                console.log('◊û◊¶◊ë ◊î◊õ◊§◊™◊ï◊®:', isValid);
                return isValid ? (
                  <button 
                    className="signup-button"
                    onClick={handleContinue}
                  >
                    ◊î◊û◊©◊ö
                  </button>
                ) : (
                  <div className="form-statu">

                  </div>
                );
              })()}
            </div>
          </div>
        </div>
  );
}

export default SignUpScreen;
