import React, { useState } from 'react';
import './App.css';
import SignUpScreen from './SignUpScreen.js';
import SignUpPreferences from './SignUpPreferences.js';
import WelcomeScreen from './WelcomeScreen.js';
import './mobile-fix.css';
function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentScreen, setCurrentScreen] = useState('login');
  const [userBasicData, setUserBasicData] = useState(null);
  const [userName, setUserName] = useState('');
  const [password, setPassword] = useState('');
  const [loginMessage, setLoginMessage] = useState(''); 
  const [loggedInUser, setLoggedInUser] = useState(null);
  const [isLoading, setIsLoading] = useState(false); 

  const handleLogin = async () => {
    if (!userName.trim() || !password.trim()) {
      setLoginMessage('  转  砖转');
      return;
    }

    setIsLoading(true); 
    setLoginMessage('转专 砖专转...'); 

    try {
      // 爪专转 AbortController 注 timeout 砖 30 砖转
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 30000);

      const response = await fetch('https://wolfit-gym-backend-ijvq.onrender.com/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userName: userName.trim(),
          password: password
        }),
        signal: controller.signal
      });

      clearTimeout(timeoutId);

      const result = await response.json();

      if (result.success) {
        console.log('转专转 爪:', result.user);
        setLoggedInUser(result.user); 
        setIsLoggedIn(true);
        setLoginMessage(''); 
      } else {
        setLoginMessage(result.message || '砖 转专转');
      }

    } catch (error) {
      console.error('砖 专 砖专转:', error);
      if (error.name === 'AbortError') {
        setLoginMessage('砖专转  . 住 砖 (转 砖 "砖" 爪专 转注专专)');
      } else {
        setLoginMessage('砖 专 砖专转. 住 砖.');
      }
    } finally {
      setIsLoading(false); 
    }
  };

  const handleGoToSignUp = () => {
    console.log('注专 住 专砖');
    setCurrentScreen('signup');
  };

  const handleBackToLogin = () => {
    console.log('专 住 转专转');
    setCurrentScreen('login');
    setUserBasicData(null);
    setUserName('');
    setPassword('');
    setLoginMessage('');
  };

  const handleSignUpContinue = (basicData) => {
    console.log('砖 转 住住:', basicData);
    setUserBasicData(basicData); 
    setCurrentScreen('signupPreferences');
  };

  const handleCompleteSignUp = async (completeUserData) => {
    console.log('专砖 砖 爪!');
    console.log('转 砖转砖 :', completeUserData);

    try {
      const response = await fetch('https://wolfit-gym-backend-ijvq.onrender.com/api/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(completeUserData)
      });

      const result = await response.json();

      if (result.success) {
        alert('专砖 砖 爪! 转  转专 注砖');
        setCurrentScreen('login');
        setUserBasicData(null);
      } else {
        alert('砖 专砖: ' + result.message);
      }

    } catch (error) {
      console.error('砖 专砖:', error);
      alert('砖 专 砖专转');
    }
  };

  const handleBackToSignUp = () => {
    console.log('专 住 专砖');
    setCurrentScreen('signup');
  };

  if (isLoggedIn) {
    return <WelcomeScreen user={loggedInUser} />; 
  }

  if (currentScreen === 'signup') {
    return (
      <SignUpScreen 
        onBackToLogin={handleBackToLogin}
        onSignUpComplete={handleSignUpContinue}
      />
    );
  }

  if (currentScreen === 'signupPreferences') {
    return (
      <SignUpPreferences 
        onBackClick={handleBackToSignUp}
        onCompleteSignUp={handleCompleteSignUp}
        userBasicData={userBasicData}
      />
    );
  }

  //  住 转专转 砖 注 State
  return (
    <div className="App">
      <div className="logo-container">
        <img src="/logo1.png" alt="WOLFit Logo" className="login-logo" />
      </div>

      <div className="login-form">
        <input 
          type="text" 
          placeholder="砖 砖转砖"  
          value={userName}
          onChange={(e) => setUserName(e.target.value)}
          disabled={isLoading}
        />
        <input 
          type="password" 
          placeholder="住住"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          disabled={isLoading}
        />

        {/* 注 砖转砖 */}
        {loginMessage && (
          <p style={{ 
            color: loginMessage.includes('砖') ? '#ff6b6b' : '#b38ed8',
            textAlign: 'center',
            margin: '10px 0'
          }}>
            {loginMessage}
          </p>
        )}

        <button 
          onClick={handleLogin}
          disabled={isLoading}
          style={{
            opacity: isLoading ? 0.6 : 1,
            cursor: isLoading ? 'not-allowed' : 'pointer',
            position: 'relative'
          }}
        >
          {isLoading ? (
            <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
              <div style={{
                width: '16px',
                height: '16px',
                border: '2px solid #ffffff',
                borderTop: '2px solid transparent',
                borderRadius: '50%',
                animation: 'spin 1s linear infinite',
                marginLeft: '8px'
              }}></div>
              转专...
            </div>
          ) : '住'}
        </button>

        <p>  砖?
          <span 
            onClick={handleGoToSignUp}
            style={{
              color: '#b38ed8',
              cursor: 'pointer',
              marginRight: '5px',
              textDecoration: 'underline'
            }}
          >
            专砖 
          </span>
        </p>
      </div>
    </div>
  );
}

export default App;
