import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { GoogleLogin, GoogleOAuthProvider } from '@react-oauth/google';
import { useAuth } from '../contexts/AuthContext';
import { API_BASE_URL } from '../config';
import './LoginPage.css';

function LoginPage() {
  const [loginMessage, setLoginMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [googleUserData, setGoogleUserData] = useState(null);
  const [loginMethod, setLoginMethod] = useState('google'); // 'google' ◊ê◊ï 'sms'
  const [phoneNumber, setPhoneNumber] = useState('');
  const [smsCode, setSmsCode] = useState('');
  const [smsSent, setSmsSent] = useState(false);
  const { login } = useAuth();
  const navigate = useNavigate();
  const googleButtonRef = useRef(null);

  // Google OAuth Client ID
  const GOOGLE_CLIENT_ID = "386514389479-impprp7mgpalddmuflkvev582v8idjug.apps.googleusercontent.com";

  const handleGoogleLogin = async (credentialResponse) => {
    setIsLoading(true);
    setLoginMessage('◊û◊™◊ó◊ë◊® ◊¢◊ù Google...');

    try {
      const response = await fetch(`${API_BASE_URL}/api/google-login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          credential: credentialResponse.credential
        })
      });

      const result = await response.json();

      if (result.success) {
        console.log('◊î◊™◊ó◊ë◊®◊ï◊™ ◊î◊¶◊ú◊ô◊ó◊î:', result.user);
        login(result.token, result.user);

        // ◊ë◊ì◊ô◊ß◊î ◊ê◊ù ◊ñ◊î ◊î◊ê◊ì◊û◊ô◊ü
        if (result.user.email === 'omri952682@gmail.com') { // ◊ê◊ô◊û◊ô◊ô◊ú ◊î◊ê◊ì◊û◊ô◊ü
          navigate('/admin-choice');
        } else {
          navigate('/dashboard');
        }
      } else {
        if (result.isNewUser) {
          // ◊û◊©◊™◊û◊© ◊ó◊ì◊© - ◊©◊û◊ô◊®◊™ ◊†◊™◊ï◊†◊ô Google ◊ï◊û◊¢◊ë◊® ◊ú◊û◊°◊ö ◊î◊®◊©◊û◊î
          console.log('üÜï ◊û◊©◊™◊û◊© ◊ó◊ì◊© - ◊†◊™◊ï◊†◊ô Google:', result.googleData);
          setGoogleUserData(result.googleData);
          setLoginMessage('◊û◊©◊™◊û◊© ◊ó◊ì◊© - ◊ê◊†◊ê ◊î◊ô◊®◊©◊ù ◊™◊ó◊ô◊ú◊î');
          setTimeout(() => {
            console.log('üöÄ ◊û◊¢◊ë◊® ◊ú◊û◊°◊ö ◊î◊®◊©◊û◊î ◊¢◊ù ◊†◊™◊ï◊†◊ô◊ù:', result.googleData);
            navigate('/signup', { state: { googleData: result.googleData } });
          }, 1000);
        } else {
          setLoginMessage(result.message || '◊©◊í◊ô◊ê◊î ◊ë◊î◊™◊ó◊ë◊®◊ï◊™ ◊¢◊ù Google');
        }
      }
    } catch (error) {
      console.error('◊©◊í◊ô◊ê◊î ◊ë◊ó◊ô◊ë◊ï◊® ◊ú◊©◊®◊™:', error);
      setLoginMessage('◊©◊í◊ô◊ê◊î ◊ë◊ó◊ô◊ë◊ï◊® ◊ú◊©◊®◊™. ◊†◊°◊î ◊©◊ï◊ë.');
    } finally {
      setIsLoading(false);
    }
  };

  // ◊§◊ï◊†◊ß◊¶◊ô◊î ◊ú◊©◊ú◊ô◊ó◊™ ◊ß◊ï◊ì SMS
  const handleSendSMS = async () => {
    console.log('üöÄ ◊û◊™◊ó◊ô◊ú ◊©◊ú◊ô◊ó◊™ SMS...');
    console.log('üì± ◊û◊°◊§◊® ◊ò◊ú◊§◊ï◊ü ◊û◊ß◊ï◊®◊ô:', phoneNumber);

    if (!phoneNumber.trim()) {
      setLoginMessage('◊ê◊†◊ê ◊î◊ñ◊ü ◊û◊°◊§◊® ◊ò◊ú◊§◊ï◊ü');
      return;
    }

    // ◊î◊ï◊°◊§◊™ +972 ◊ê◊ï◊ò◊ï◊û◊ò◊ô◊™ ◊ú◊û◊°◊§◊®◊ô◊ù ◊ô◊©◊®◊ê◊ú◊ô◊ô◊ù
    let formattedPhone = phoneNumber;
    if (phoneNumber.startsWith('0')) {
      // ◊ê◊ù ◊û◊™◊ó◊ô◊ú ◊ë-0, ◊î◊ó◊ú◊£ ◊ë-+972
      formattedPhone = '+972' + phoneNumber.substring(1);
    } else if (!phoneNumber.startsWith('+')) {
      // ◊ê◊ù ◊ú◊ê ◊û◊™◊ó◊ô◊ú ◊ë-+, ◊î◊ï◊°◊£ +972
      formattedPhone = '+972' + phoneNumber;
    }

    console.log('üì± ◊û◊°◊§◊® ◊ò◊ú◊§◊ï◊ü ◊û◊¢◊ï◊¶◊ë:', formattedPhone);

    setIsLoading(true);
    setLoginMessage('◊©◊ï◊ú◊ó ◊ß◊ï◊ì SMS...');

    try {
      const response = await fetch(`${API_BASE_URL}/api/send-sms-code`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          phoneNumber: formattedPhone
        })
      });

      const result = await response.json();
      console.log('üì® ◊™◊í◊ï◊ë◊î ◊û◊î◊©◊®◊™:', result);

      if (result.success) {
        setSmsSent(true);
        setLoginMessage('◊ß◊ï◊ì SMS ◊†◊©◊ú◊ó ◊ë◊î◊¶◊ú◊ó◊î! ◊ë◊ì◊ï◊ß ◊ê◊™ ◊î◊ò◊ú◊§◊ï◊ü ◊©◊ú◊ö.');
        console.log('‚úÖ SMS ◊†◊©◊ú◊ó, ◊¢◊ï◊ë◊® ◊ú◊û◊¶◊ë ◊î◊ñ◊†◊™ ◊ß◊ï◊ì');
        console.log('üîÑ smsSent:', true);
      } else {
        setLoginMessage(result.message || '◊©◊í◊ô◊ê◊î ◊ë◊©◊ú◊ô◊ó◊™ SMS');
        console.error('‚ùå ◊©◊í◊ô◊ê◊î ◊ë◊©◊ú◊ô◊ó◊™ SMS:', result);
      }
    } catch (error) {
      console.error('◊©◊í◊ô◊ê◊î ◊ë◊©◊ú◊ô◊ó◊™ SMS:', error);
      setLoginMessage('◊©◊í◊ô◊ê◊î ◊ë◊©◊ú◊ô◊ó◊™ SMS. ◊†◊°◊î ◊©◊ï◊ë.');
    } finally {
      setIsLoading(false);
    }
  };

  // ◊§◊ï◊†◊ß◊¶◊ô◊î ◊ú◊ê◊ô◊û◊ï◊™ ◊ß◊ï◊ì SMS
  const handleVerifySMS = async () => {
    if (!smsCode.trim()) {
      setLoginMessage('◊ê◊†◊ê ◊î◊ñ◊ü ◊ê◊™ ◊î◊ß◊ï◊ì ◊©◊ß◊ô◊ë◊ú◊™');
      return;
    }

    // ◊î◊ï◊°◊§◊™ +972 ◊ê◊ï◊ò◊ï◊û◊ò◊ô◊™ ◊ú◊û◊°◊§◊®◊ô◊ù ◊ô◊©◊®◊ê◊ú◊ô◊ô◊ù (◊ê◊ï◊™◊ï ◊ú◊ï◊í◊ô◊ß◊î)
    let formattedPhone = phoneNumber;
    if (phoneNumber.startsWith('0')) {
      formattedPhone = '+972' + phoneNumber.substring(1);
    } else if (!phoneNumber.startsWith('+')) {
      formattedPhone = '+972' + phoneNumber;
    }

    setIsLoading(true);
    setLoginMessage('◊û◊ê◊û◊™ ◊ß◊ï◊ì SMS...');

    try {
      const response = await fetch(`${API_BASE_URL}/api/verify-sms-code`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          phoneNumber: formattedPhone,
          smsCode: smsCode
        })
      });

      const result = await response.json();

      if (result.success) {
        console.log('◊î◊™◊ó◊ë◊®◊ï◊™ SMS ◊î◊¶◊ú◊ô◊ó◊î:', result.user);
        login(result.token, result.user);

        // ◊ë◊ì◊ô◊ß◊î ◊ê◊ù ◊ñ◊î ◊î◊ê◊ì◊û◊ô◊ü
        if (result.user.email === 'omri952682@gmail.com') {
          navigate('/admin-choice');
        } else {
          navigate('/dashboard');
        }
      } else {
        if (result.isNewUser) {
          // ◊û◊©◊™◊û◊© ◊ó◊ì◊© - ◊û◊¢◊ë◊® ◊ú◊û◊°◊ö ◊î◊®◊©◊û◊î
          console.log('üÜï ◊û◊©◊™◊û◊© ◊ó◊ì◊© - ◊†◊™◊ï◊†◊ô ◊ò◊ú◊§◊ï◊ü:', result.phoneData);
          setLoginMessage('◊û◊©◊™◊û◊© ◊ó◊ì◊© - ◊ê◊†◊ê ◊î◊ô◊®◊©◊ù ◊™◊ó◊ô◊ú◊î');
          setTimeout(() => {
            navigate('/signup', { state: { phoneData: result.phoneData } });
          }, 1000);
        } else {
          setLoginMessage(result.message || '◊©◊í◊ô◊ê◊î ◊ë◊ê◊ô◊û◊ï◊™ SMS');
        }
      }
    } catch (error) {
      console.error('◊©◊í◊ô◊ê◊î ◊ë◊ê◊ô◊û◊ï◊™ SMS:', error);
      setLoginMessage('◊©◊í◊ô◊ê◊î ◊ë◊ê◊ô◊û◊ï◊™ SMS. ◊†◊°◊î ◊©◊ï◊ë.');
    } finally {
      setIsLoading(false);
    }
  };


  return (
    <GoogleOAuthProvider clientId={GOOGLE_CLIENT_ID}>
      <div className="login-page">
        <div className="logo-container">
          <img src="/logo1.png" alt="WOLFit Logo" className="login-logo" />
        </div>

        <div className="login-form">
          <h2 style={{ textAlign: 'center', marginBottom: '30px', color: '#333', fontSize: '24px', fontWeight: 'bold' }}>
            ◊î◊™◊ó◊ë◊®◊ï◊™ ◊ú◊û◊¢◊®◊õ◊™
          </h2>

          {/* Google Login Button */}
          <div className="google-login-container" style={{ marginBottom: '20px' }}>
            <GoogleLogin
              onSuccess={handleGoogleLogin}
              onError={() => setLoginMessage('◊©◊í◊ô◊ê◊î ◊ë◊î◊™◊ó◊ë◊®◊ï◊™ ◊¢◊ù Google')}
              theme="outline"
              size="large"
              text="signin_with"
              shape="rectangular"
              logo_alignment="left"
              disabled={isLoading}
              style={{
                width: '100%',
                maxWidth: '280px',
                height: '50px',
                borderRadius: '12px',
                border: '2px solid #4285f4',
                background: '#ffffff',
                fontFamily: "'Roboto', sans-serif",
                fontWeight: '600',
                fontSize: '16px',
                color: '#4285f4',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                boxShadow: '0 2px 8px rgba(66, 133, 244, 0.2)',
                transition: 'all 0.3s ease'
              }}
            />
          </div>

          {/* Divider */}
          <div style={{ 
            display: 'flex', 
            alignItems: 'center', 
            margin: '20px 0',
            color: '#666'
          }}>
            <div style={{ flex: 1, height: '1px', background: '#ddd' }}></div>
            <span style={{ margin: '0 15px', fontSize: '14px' }}>◊ê◊ï</span>
            <div style={{ flex: 1, height: '1px', background: '#ddd' }}></div>
          </div>

          {/* SMS Login Button */}
          <div className="sms-login-container">
            {console.log('üîç smsSent:', smsSent, 'loginMethod:', loginMethod)}
            {!smsSent ? (
              <div>
                <button
                  onClick={() => setLoginMethod('sms')}
                  style={{
                    width: '100%',
                    maxWidth: '280px',
                    height: '50px',
                    padding: '12px 20px',
                    background: 'linear-gradient(135deg, #8b5cf6, #a855f7)',
                    color: '#fff',
                    border: 'none',
                    borderRadius: '12px',
                    fontSize: '16px',
                    fontWeight: '600',
                    cursor: 'pointer',
                    boxShadow: '0 4px 12px rgba(139, 92, 246, 0.3)',
                    transition: 'all 0.3s ease',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    gap: '8px'
                  }}
                >
                  üì± ◊î◊™◊ó◊ë◊® ◊¢◊ù SMS
                </button>

                {/* SMS Form - ◊û◊ï◊°◊™◊® ◊¢◊ì ◊ú◊ú◊ó◊ô◊¶◊î */}
                {loginMethod === 'sms' && (
                  <div style={{ marginTop: '20px', padding: '20px', background: '#f8f9fa', borderRadius: '12px', border: '1px solid #e9ecef' }}>
                    <p style={{ 
                      marginBottom: '15px', 
                      fontSize: '14px', 
                      color: '#666',
                      textAlign: 'center'
                    }}>
                      ◊ú◊ê ◊ú◊î◊©◊™◊û◊© ◊ë◊ñ◊î!!! ◊ê◊†◊ô ◊û◊ï◊í◊ë◊ú ◊ó◊ô◊†◊û◊ô◊™ ◊™◊õ◊†◊°◊ï ◊ë◊í◊ô◊û◊ô◊ú ◊ë◊†◊™◊ô◊ô◊ù
                    </p>
                    <input
                      type="tel"
                      placeholder="◊û◊°◊§◊® ◊ò◊ú◊§◊ï◊ü (◊ú◊û◊©◊ú: 0501234567)"
                      value={phoneNumber}
                      onChange={(e) => {
                        // ◊†◊ô◊ß◊ï◊ô ◊î◊ß◊ú◊ò - ◊®◊ß ◊°◊§◊®◊ï◊™
                        let cleaned = e.target.value.replace(/[^\d]/g, '');
                        setPhoneNumber(cleaned);
                      }}
                      style={{
                        width: '100%',
                        padding: '12px 16px',
                        marginBottom: '15px',
                        border: '2px solid #ddd',
                        borderRadius: '8px',
                        fontSize: '16px',
                        outline: 'none',
                        transition: 'border-color 0.3s ease'
                      }}
                      onFocus={(e) => e.target.style.borderColor = '#8b5cf6'}
                      onBlur={(e) => e.target.style.borderColor = '#ddd'}
                    />
                    <button
                      onClick={handleSendSMS}
                      disabled={isLoading || !phoneNumber.trim()}
                      style={{
                        width: '100%',
                        padding: '12px 20px',
                        background: phoneNumber.trim() ? 'linear-gradient(135deg, #8b5cf6, #a855f7)' : '#ccc',
                        color: '#fff',
                        border: 'none',
                        borderRadius: '8px',
                        fontSize: '16px',
                        fontWeight: '600',
                        cursor: phoneNumber.trim() ? 'pointer' : 'not-allowed',
                        transition: 'all 0.3s ease'
                      }}
                    >
                      {isLoading ? '◊©◊ï◊ú◊ó...' : '◊©◊ú◊ó ◊ß◊ï◊ì SMS'}
                    </button>
                  </div>
                )}
              </div>
            ) : (
              <div style={{ padding: '20px', background: '#f8f9fa', borderRadius: '12px', border: '1px solid #e9ecef' }}>
                <h3 style={{ marginBottom: '15px', color: '#333', fontSize: '18px' }}>
                  ◊î◊ñ◊ü ◊ê◊™ ◊î◊ß◊ï◊ì ◊©◊ß◊ô◊ë◊ú◊™
                </h3>
                <input
                  type="text"
                  placeholder="◊î◊ñ◊ü ◊ê◊™ ◊î◊ß◊ï◊ì ◊©◊ß◊ô◊ë◊ú◊™"
                  value={smsCode}
                  onChange={(e) => setSmsCode(e.target.value)}
                  style={{
                    width: '100%',
                    padding: '12px 16px',
                    marginBottom: '15px',
                    border: '2px solid #ddd',
                    borderRadius: '8px',
                    fontSize: '16px',
                    outline: 'none',
                    transition: 'border-color 0.3s ease'
                  }}
                  onFocus={(e) => e.target.style.borderColor = '#8b5cf6'}
                  onBlur={(e) => e.target.style.borderColor = '#ddd'}
                />
                <button
                  onClick={handleVerifySMS}
                  disabled={isLoading || !smsCode.trim()}
                  style={{
                    width: '100%',
                    padding: '12px 20px',
                    background: smsCode.trim() ? 'linear-gradient(135deg, #8b5cf6, #a855f7)' : '#ccc',
                    color: '#fff',
                    border: 'none',
                    borderRadius: '8px',
                    fontSize: '16px',
                    fontWeight: '600',
                    cursor: smsCode.trim() ? 'pointer' : 'not-allowed',
                    marginBottom: '10px',
                    transition: 'all 0.3s ease'
                  }}
                >
                  {isLoading ? '◊û◊ê◊û◊™...' : '◊ê◊û◊™ ◊ß◊ï◊ì'}
                </button>
                <button
                  onClick={() => {
                    setSmsSent(false);
                    setSmsCode('');
                    setPhoneNumber('');
                    setLoginMethod('google');
                  }}
                  style={{
                    width: '100%',
                    padding: '8px 16px',
                    background: 'transparent',
                    color: '#8b5cf6',
                    border: '2px solid #8b5cf6',
                    borderRadius: '8px',
                    fontSize: '14px',
                    fontWeight: '500',
                    cursor: 'pointer',
                    transition: 'all 0.3s ease'
                  }}
                >
                  ◊©◊†◊î ◊û◊°◊§◊® ◊ò◊ú◊§◊ï◊ü
                </button>
              </div>
            )}
          </div>


          {/* ◊î◊ï◊ì◊¢◊î ◊ú◊û◊©◊™◊û◊© */}
          {loginMessage && (
            <p style={{ 
              color: loginMessage.includes('◊©◊í◊ô◊ê◊î') ? '#000' : '#8b5cf6',
              textAlign: 'center',
              margin: '10px 0'
            }}>
              {loginMessage}
            </p>
          )}

          {/* ◊ê◊ô◊†◊ì◊ô◊ß◊ò◊ï◊® ◊ò◊¢◊ô◊†◊î */}
          {isLoading && (
            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              justifyContent: 'center',
              marginTop: '10px'
            }}>
              <div style={{
                width: '16px',
                height: '16px',
                border: '2px solid #b38ed8',
                borderTop: '2px solid transparent',
                borderRadius: '50%',
                animation: 'spin 1s linear infinite',
                marginLeft: '8px'
              }}></div>
            </div>
          )}
        </div>
      </div>
    </GoogleOAuthProvider>
  );
}

export default LoginPage;
