import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from './contexts/AuthContext';
import './EditUser.css';
import { API_BASE_URL } from './config';
import FitnessMetricsChart from './components/FitnessMetricsChart';
import InteractiveBodySelector from './components/InteractiveBodySelector';


function EditUser() {
  const { user: currentUser } = useAuth();
  const navigate = useNavigate();
  const [selectedSports, setSelectedSports] = useState([]);
  const [preferenceMode, setPreferenceMode] = useState('simple');
  const [intensityLevel, setIntensityLevel] = useState(2);
  const [isLoading, setIsLoading] = useState(false);

  // ×©×“×•×ª ×—×“×©×™× ×œ×‘×—×™×¨×ª ××–×•×¨×™ ×’×•×£ ×•××¨×›×™×‘×™ ×›×©×™×¨×•×ª (×œ× × ×©××¨×™× ×‘××¡×“ × ×ª×•× ×™×)
  const [wantsStrengthTraining, setWantsStrengthTraining] = useState(false);
  const [selectedBodyAreas, setSelectedBodyAreas] = useState([]);
  const [selectedFitnessComponents, setSelectedFitnessComponents] = useState([]);

  const [saveMessage, setSaveMessage] = useState('');
  const [isSaving, setIsSaving] = useState(false);

  // ××¦×‘ ×œ× ×™×”×•×œ ×’×¨×£ ××“×“×™ ×”×›×•×©×¨
  const [chartOpen, setChartOpen] = useState(false);
  const [selectedSportForChart, setSelectedSportForChart] = useState(null);

  const SPORTS_LIST = [
    { id: 1, name: '×›×“×•×¨×’×œ', icon: '', image: '/images/sports/football.jpg' },
    { id: 2, name: '×›×“×•×¨×¡×œ', icon: '', image: '/images/sports/basketball.jpg' },
    { id: 3, name: '×˜×™×¤×•×¡', icon: '', image: '/images/sports/climbing.jpg' },
    { id: 4, name: '×—×“×¨ ×›×•×©×¨', icon: '', image: '/images/sports/gym.jpg' },
    { id: 5, name: '×§×•×¨×“×™× ×¦×™×”', icon: '', image: '/images/sports/coordination.jpg' },
    { id: 6, name: '×˜× ×™×¡', icon: '', image: '/images/sports/tennis.jpg' },
    { id: 7, name: '×¤×™× ×’×¤×•× ×’', icon: '', image: '/images/sports/pingpong.jpg' },
    { id: 8, name: '××’×¨×•×£', icon: '', image: '/images/sports/dance.jpg' },
    { id: 9, name: '××•×¤× ×™×™×', icon: '', image: '/images/sports/cycling.jpg' }
  ];


  const loadUserPreferences = async () => {
    if (!currentUser || !currentUser.id) {
      console.log('××™×Ÿ ××©×ª××© × ×•×›×—×™');
      return;
    }

    console.log('×˜×•×¢×Ÿ ×”×¢×“×¤×•×ª ×¢×‘×•×¨ ××©×ª××©:', currentUser.id);
    setIsLoading(true);

    try {
      console.log('ğŸ” ××ª×—×™×œ ×œ×˜×¢×•×Ÿ ×”×¢×“×¤×•×ª ×¢×‘×•×¨ ××©×ª××©:', currentUser.id);
      const token = localStorage.getItem('authToken');
      console.log('ğŸ”‘ × ××¦× ×˜×•×§×Ÿ:', token ? '×›×Ÿ' : '×œ×');

      const response = await fetch(`${API_BASE_URL}/api/user-preferences/${currentUser.id}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });
      console.log('ğŸ“¡ ×ª×’×•×‘×” ××”×©×¨×ª:', {
        status: response.status,
        ok: response.ok,
        statusText: response.statusText
      });

      if (response.ok) {
        const result = await response.json();
        console.log('× ×ª×•× ×™× ×©×”×ª×§×‘×œ×•:', result);

          if (result.success && result.data) {
            console.log('âœ… ×”×ª×§×‘×œ×• × ×ª×•× ×™× ×ª×§×™× ×™× ××”×©×¨×ª');
            const { intensityLevel, sports, preferenceMode, strengthTraining } = result.data;

            console.log('×¨××ª ×¢×¦×™××•×ª:', intensityLevel);
            console.log('×›×œ ×”×¡×¤×•×¨×˜×™×:', sports);
            console.log('ğŸ”„ ××¦×‘ ×”×¢×“×¤×”:', preferenceMode);

            console.log('ğŸ“ ××ª×—×™×œ ×œ×¢×‘×“ ××ª ×”× ×ª×•× ×™×...');

            // ××—×œ×¥ ××ª ×”×¡×¤×•×¨×˜×™× ×”× ×‘×—×¨×™×
            // ××—×œ×¥ ××ª ×”×¡×¤×•×¨×˜×™× ×”× ×‘×—×¨×™×
            let selectedIds;
            if (Array.isArray(sports)) {
              // ×× ×–×” ××¢×¨×š ×©×œ ××•×‘×™×™×§×˜×™× ×¢× selected
              if (sports.some(sport => 'selected' in sport)) {
                selectedIds = sports
                  .filter(sport => sport.selected)
                  .sort((a, b) => (a.rank || 0) - (b.rank || 0))
                  .map(sport => sport.id);
              } 
              // ×× ×–×” ××¢×¨×š ×©×œ ××•×‘×™×™×§×˜×™× ×¢× id ×™×©×™×¨×•×ª
              else {
                selectedIds = sports
                  .sort((a, b) => (a.rank || 0) - (b.rank || 0))
                  .map(sport => sport.id);
              }
            } else {
              selectedIds = [];
            }

            console.log('×¡×¤×•×¨×˜×™× × ×‘×—×¨×™×:', selectedIds);

            setIntensityLevel(intensityLevel || 2);
            setSelectedSports(selectedIds);
            setPreferenceMode(preferenceMode || 'simple');

            // ×˜×¢×™× ×ª × ×ª×•× ×™ ××™××•×Ÿ ×›×•×—
            console.log('ğŸ’ª ×‘×•×“×§ × ×ª×•× ×™ ××™××•×Ÿ ×›×•×—:', strengthTraining);
            if (strengthTraining) {
              console.log('ğŸ’ª ×˜×•×¢×Ÿ × ×ª×•× ×™ ××™××•×Ÿ ×›×•×—:', strengthTraining);
              console.log('ğŸ’ª wantsStrengthTraining:', strengthTraining.wantsStrengthTraining);
              console.log('ğŸ’ª selectedBodyAreas:', strengthTraining.selectedBodyAreas);
              console.log('ğŸ’ª selectedFitnessComponents:', strengthTraining.selectedFitnessComponents);

              setWantsStrengthTraining(strengthTraining.wantsStrengthTraining || false);
              setSelectedBodyAreas(strengthTraining.selectedBodyAreas || []);
              setSelectedFitnessComponents(strengthTraining.selectedFitnessComponents || []);

              console.log('ğŸ’ª State ×¢×•×“×›×Ÿ - wantsStrengthTraining:', strengthTraining.wantsStrengthTraining);
              console.log('ğŸ’ª State ×¢×•×“×›×Ÿ - selectedBodyAreas:', strengthTraining.selectedBodyAreas);
              console.log('ğŸ’ª State ×¢×•×“×›×Ÿ - selectedFitnessComponents:', strengthTraining.selectedFitnessComponents);
            } else {
              console.log('ğŸ’ª ××™×Ÿ × ×ª×•× ×™ ××™××•×Ÿ ×›×•×— ××”×©×¨×ª');
            }

          console.log('State ×¢×•×“×›×Ÿ ×‘×”×¦×œ×—×”');
        } else {
          console.log('××™×Ÿ ×”×¢×“×¤×•×ª ×§×™×™××•×ª ××• ×ª×’×•×‘×” ×œ× ×ª×§×™× ×”');
          setSelectedSports([]);
          setPreferenceMode('simple');
          setIntensityLevel(2);
