import React, { useState, useEffect } from 'react';

import './CreateWorkout.css';
import { API_BASE_URL } from './config';

// ××™×¤×•×™ ×¡×¤×•×¨×˜×™× (×ª×•×× ×œ×©×¨×ª ×©×œ×š)
const SPORT_MAPPING = {
  1: '×›×“×•×¨×’×œ',
  2: '×›×“×•×¨×¡×œ',  
  3: '×˜×™×¤×•×¡',
  4: '×—×“×¨ ×›×•×©×¨',
  5: '×§×•×¨×“×™× ×¦×™×”',
  6: '×˜× ×™×¡',
  7: '×¤×™× ×’×¤×•× ×’',
  8: '×¨×™×§×•×“',
  9: '××•×¤× ×™×™×'
};

// ×”××œ×’×•×¨×™×ª× ×”×”×•× ×’×¨×™ ×”×•×¢×‘×¨ ×œ×©×¨×ª - ×”×§×•×“ ×‘×¦×“ ×”×œ×§×•×— ×¨×§ ×©×•×œ×— ×‘×§×©×•×ª

// ×”×¨×›×™×‘ ×”×¨××©×™ ×¢× ×”×©×œ××•×ª ××œ××•×ª
function CreateWorkout({ user, selectedDate, startTime, endTime, onBackClick }) {
  const [isGenerating, setIsGenerating] = useState(false);
  const [workoutPlan, setWorkoutPlan] = useState(null);
  const [userPreferences, setUserPreferences] = useState([]);
  // fieldsByTime ×›×‘×¨ ×œ× × ×—×•×¥ - ×”×©×¨×ª ××˜×¤×œ ×‘×–×”
  const [timeSlots, setTimeSlots] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [isSaving, setIsSaving] = useState(false);
  const [saveSuccess, setSaveSuccess] = useState(false);

  useEffect(() => {
    console.log('ğŸ¬ CreateWorkout × ×˜×¢×Ÿ ×¢× ×¤×¨××˜×¨×™×:', {
      user: user?.userName,
      selectedDate,
      startTime,
      endTime
    });

    initializeWorkoutData();
  }, []);

  const initializeWorkoutData = async () => {
    try {
      setLoading(true);
      setError('');

      console.log('ğŸ“Š ×˜×•×¢×Ÿ × ×ª×•× ×™ ××©×ª××©...');
      await loadUserPreferences();

      console.log('â° ×™×•×¦×¨ ×¨×‘×¢×™ ×©×¢×”...');
      const slots = createTimeSlots();
      setTimeSlots(slots);

      console.log('âœ… ×›×œ ×”× ×ª×•× ×™× × ×˜×¢× ×• ×‘×”×¦×œ×—×”');

    } catch (err) {
      console.error('âŒ ×©×’×™××” ×‘×˜×¢×™× ×ª × ×ª×•× ×™×:', err);
      setError('×©×’×™××” ×‘×˜×¢×™× ×ª × ×ª×•× ×™×. ×× × × ×¡×” ×©×•×‘.');
    } finally {
      setLoading(false);
    }
  };

  const loadUserPreferences = async () => {
    try {
      if (!user || !user.id) {
        throw new Error('××©×ª××© ×œ× ××•×’×“×¨');
      }

      const url = `https://wolfit-gym-backend-ijvq.onrender.com/api/user-preferences/${user.id}`;
      console.log('ğŸ“¡ ×§×•×¨× ×”×¢×“×¤×•×ª ×:', url);

      const response = await fetch(url);
      const data = await response.json();

      if (data.success) {
        const preferences = data.data.selectedSports || [];
        setUserPreferences(preferences);
        console.log('â¤ï¸ ×”×¢×“×¤×•×ª × ×˜×¢× ×•:', preferences.map(id => SPORT_MAPPING[id]).join(', '));
      } else {
        console.log('âš ï¸ ××™×Ÿ ×”×¢×“×¤×•×ª ×©××•×¨×•×ª');
        setUserPreferences([]);
      }
    } catch (error) {
      console.error('âŒ ×©×’×™××” ×‘×˜×¢×™× ×ª ×”×¢×“×¤×•×ª:', error);
      setUserPreferences([]);
      throw error;
    }
  };

  const createTimeSlots = () => {
    const slots = [];
    const [startHour, startMinute] = startTime.split(':').map(Number);
    const [endHour, endMinute] = endTime.split(':').map(Number);
    const startTotalMinutes = startHour * 60 + startMinute;
    const endTotalMinutes = endHour * 60 + endMinute;

    console.log(`â° ×™×•×¦×¨ ×¨×‘×¢×™ ×©×¢×” ×-${startTime} ×¢×“ ${endTime}`);

    for (let minutes = startTotalMinutes; minutes < endTotalMinutes; minutes += 15) {
      const hours = Math.floor(minutes / 60);
      const mins = minutes % 60;
      const timeStr = `${hours.toString().padStart(2, '0')}:${mins.toString().padStart(2, '0')}`;
      slots.push(timeStr);
    }

    console.log(`ğŸ“… × ×•×¦×¨×• ${slots.length} ×¨×‘×¢×™ ×©×¢×”:`, slots.join(', '));
    return slots;
  };

  // loadAvailableFields ×”×•×¡×¨×” - ×”×©×¨×ª ××˜×¤×œ ×‘×–×” ×›×¢×ª

  const generateOptimalWorkout = async () => {
    console.log('ğŸ¯ ××ª×—×™×œ ×™×¦×™×¨×ª ×ª×•×›× ×™×ª ××™××•×Ÿ ××•×¤×˜×™××œ×™×ª...');

    if (timeSlots.length === 0) {
      console.log('âŒ ××™×Ÿ ×–×× ×™× ×–××™× ×™× ×œ×™×¦×™×¨×ª ××™××•×Ÿ');
      return null;
    }

    try {
      console.log('ğŸ“¡ ×©×•×œ×— ×‘×§×©×” ×œ×©×¨×ª ×œ×™×¦×™×¨×ª ××™××•×Ÿ ××•×¤×˜×™××œ×™...');

      const requestBody = {
        userId: user.id,
        date: selectedDate,
        timeSlots: timeSlots,
        userPreferences: userPreferences
      };

      console.log('ğŸ“‹ × ×ª×•× ×™ ×‘×§×©×”:', requestBody);

      const response = await fetch('http://localhost:3001/api/generate-optimal-workout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestBody)
      });

      if (!response.ok) {
