import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from './contexts/AuthContext';
import './EditUser.css';
import { API_BASE_URL } from './config';
import FitnessMetricsChart from './components/FitnessMetricsChart';


function EditUser() {
  const { user: currentUser } = useAuth();
  const navigate = useNavigate();
  const [selectedSports, setSelectedSports] = useState([]);
  const [preferenceMode, setPreferenceMode] = useState('simple');
  const [intensityLevel, setIntensityLevel] = useState(2);
  const [isLoading, setIsLoading] = useState(false);

  // ×©×“×•×ª ×—×“×©×™× ×œ×‘×—×™×¨×ª ××–×•×¨×™ ×’×•×£
  const [wantsStrengthTraining, setWantsStrengthTraining] = useState(false);
  const [selectedBodyAreas, setSelectedBodyAreas] = useState([]);

  const [saveMessage, setSaveMessage] = useState('');
  const [isSaving, setIsSaving] = useState(false);

  // ×ž×¦×‘ ×œ× ×™×”×•×œ ×’×¨×£ ×ž×“×“×™ ×”×›×•×©×¨
  const [chartOpen, setChartOpen] = useState(false);
  const [selectedSportForChart, setSelectedSportForChart] = useState(null);

  const SPORTS_LIST = [
    { id: 1, name: '×›×“×•×¨×’×œ', icon: 'âš½', image: '/images/sports/football.jpg' },
    { id: 2, name: '×›×“×•×¨×¡×œ', icon: 'ðŸ€', image: '/images/sports/basketball.jpg' },
    { id: 3, name: '×˜×™×¤×•×¡', icon: 'ðŸ§—', image: '/images/sports/climbing.jpg' },
    { id: 4, name: '×—×“×¨ ×›×•×©×¨', icon: 'ðŸ‹ï¸', image: '/images/sports/gym.jpg' },
    { id: 5, name: '×§×•×¨×“×™× ×¦×™×”', icon: 'ðŸŽ¯', image: '/images/sports/coordination.jpg' },
    { id: 6, name: '×˜× ×™×¡', icon: 'ðŸŽ¾', image: '/images/sports/tennis.jpg' },
    { id: 7, name: '×¤×™× ×’×¤×•× ×’', icon: 'ðŸ“', image: '/images/sports/pingpong.jpg' },
    { id: 8, name: '××’×¨×•×£', icon: 'ðŸ¥Š', image: '/images/sports/dance.jpg' },
    { id: 9, name: '××•×¤× ×™×™×', icon: 'ðŸš´', image: '/images/sports/cycling.jpg' }
  ];

  // ×¨×©×™×ž×ª ××–×•×¨×™ ×’×•×£
  const BODY_AREAS = [
    { id: 'back', name: '×’×‘', icon: 'ðŸ¦´' },
    { id: 'shoulders', name: '×›×ª×¤×™×™×', icon: 'ðŸ’ª' },
    { id: 'arms', name: '×™×“×™×™×', icon: 'ðŸ¦¾' },
    { id: 'chest', name: '×—×–×”', icon: 'ðŸ«' },
    { id: 'core', name: '×œ×™×‘×”/×‘×˜×Ÿ', icon: 'ðŸŽ¯' },
    { id: 'legs', name: '×¨×’×œ×™×™×', icon: 'ðŸ¦µ' }
  ];

  const loadUserPreferences = async () => {
    if (!currentUser || !currentUser.id) {
      console.log('××™×Ÿ ×ž×©×ª×ž×© × ×•×›×—×™');
      return;
    }

    console.log('×˜×•×¢×Ÿ ×”×¢×“×¤×•×ª ×¢×‘×•×¨ ×ž×©×ª×ž×©:', currentUser.id);
    setIsLoading(true);

    try {
      console.log('ðŸ” ×ž×ª×—×™×œ ×œ×˜×¢×•×Ÿ ×”×¢×“×¤×•×ª ×¢×‘×•×¨ ×ž×©×ª×ž×©:', currentUser.id);
      const token = localStorage.getItem('authToken');
      console.log('ðŸ”‘ × ×ž×¦× ×˜×•×§×Ÿ:', token ? '×›×Ÿ' : '×œ×');

      const response = await fetch(`${API_BASE_URL}/api/user-preferences/${currentUser.id}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });
      console.log('ðŸ“¡ ×ª×’×•×‘×” ×ž×”×©×¨×ª:', {
        status: response.status,
        ok: response.ok,
        statusText: response.statusText
      });

      if (response.ok) {
        const result = await response.json();
        console.log('× ×ª×•× ×™× ×©×”×ª×§×‘×œ×•:', result);

          if (result.success && result.data) {
            console.log('âœ… ×”×ª×§×‘×œ×• × ×ª×•× ×™× ×ª×§×™× ×™× ×ž×”×©×¨×ª');
            const { intensityLevel, sports, preferenceMode, wantsStrengthTraining, selectedBodyAreas } = result.data;

            console.log('ðŸ’ª ×¨×ž×ª ×¢×¦×™×ž×•×ª:', intensityLevel);
            console.log('ðŸŽ¯ ×›×œ ×”×¡×¤×•×¨×˜×™×:', sports);
            console.log('ðŸ”„ ×ž×¦×‘ ×”×¢×“×¤×”:', preferenceMode);
            console.log('ðŸ’ª ××™×ž×•×Ÿ ×›×•×—:', wantsStrengthTraining);
            console.log('ðŸŽ¯ ××–×•×¨×™ ×’×•×£:', selectedBodyAreas);

            console.log('ðŸ“ ×ž×ª×—×™×œ ×œ×¢×‘×“ ××ª ×”× ×ª×•× ×™×...');

            // ×ž×—×œ×¥ ××ª ×”×¡×¤×•×¨×˜×™× ×”× ×‘×—×¨×™×
            // ×ž×—×œ×¥ ××ª ×”×¡×¤×•×¨×˜×™× ×”× ×‘×—×¨×™×
            let selectedIds;
            if (Array.isArray(sports)) {
              // ×× ×–×” ×ž×¢×¨×š ×©×œ ××•×‘×™×™×§×˜×™× ×¢× selected
              if (sports.some(sport => 'selected' in sport)) {
                selectedIds = sports
                  .filter(sport => sport.selected)
                  .sort((a, b) => (a.rank || 0) - (b.rank || 0))
                  .map(sport => sport.id);
              } 
              // ×× ×–×” ×ž×¢×¨×š ×©×œ ××•×‘×™×™×§×˜×™× ×¢× id ×™×©×™×¨×•×ª
              else {
                selectedIds = sports
                  .sort((a, b) => (a.rank || 0) - (b.rank || 0))
                  .map(sport => sport.id);
              }
            } else {
              selectedIds = [];
            }

            console.log('×¡×¤×•×¨×˜×™× × ×‘×—×¨×™×:', selectedIds);

            setIntensityLevel(intensityLevel || 2);
            setSelectedSports(selectedIds);
            setPreferenceMode(preferenceMode || 'simple');
            setWantsStrengthTraining(wantsStrengthTraining || false);
            setSelectedBodyAreas(selectedBodyAreas || []);

          console.log('State ×¢×•×“×›×Ÿ ×‘×”×¦×œ×—×”');
        } else {
          console.log('××™×Ÿ ×”×¢×“×¤×•×ª ×§×™×™×ž×•×ª ××• ×ª×’×•×‘×” ×œ× ×ª×§×™× ×”');
          setSelectedSports([]);
          setPreferenceMode('simple');
          setIntensityLevel(2);
          setWantsStrengthTraining(false);
          setSelectedBodyAreas([]);
        }
      } else {
        console.log('×©×’×™××” ×‘×ª×’×•×‘×” ×ž×”×©×¨×ª:', response.status);
        setSelectedSports([]);
        setPreferenceMode('simple');
        setIntensityLevel(2);
        setWantsStrengthTraining(false);
        setSelectedBodyAreas([]);
      }
    } catch (error) {
      console.error('×©×’×™××” ×‘×˜×¢×™× ×ª ×”×¢×“×¤×•×ª:', error);
      setSelectedSports([]);
      setPreferenceMode('simple');
      setIntensityLevel(2);
    } finally {
      setIsLoading(false);
    }
  };

  const saveUserPreferences = async () => {
    console.log('=== ×”×ª×—×œ×ª ×©×ž×™×¨×ª ×”×¢×“×¤×•×ª ===');

    if (!currentUser || !currentUser.id) {
      console.log('âŒ ××™×Ÿ ×ž×©×ª×ž×© × ×•×›×—×™ ×œ×©×ž×™×¨×”');
      return;
    }

    if (selectedSports.length === 0) {
      console.log('âŒ ××™×Ÿ ×¡×¤×•×¨×˜ × ×‘×—×¨×™×');
      setSaveMessage('×× × ×‘×—×¨ ×œ×¤×—×•×ª ×¡×¤×•×¨×˜ ××—×“');
      return;
    }

    console.log('âœ… ×ž×©×ª×ž×©:', currentUser.id);
    console.log('âœ… ×¡×¤×•×¨×˜ × ×‘×—×¨×™×:', selectedSports);
    console.log('âœ… ×ž×¦×‘ ×“×™×¨×•×’:', preferenceMode);
    console.log('âœ… ×¨×ž×ª ×¢×¦×™×ž×•×ª:', intensityLevel);

    setIsSaving(true);
    setSaveMessage('');

    try {
      const requestData = {
        intensityLevel: intensityLevel,
        selectedSports: selectedSports
      };

      console.log('ðŸ“¤ × ×ª×•× ×™× ×œ×©×œ×™×—×”:', JSON.stringify(requestData, null, 2));

      const url = `${API_BASE_URL}/api/save-user-preferences/${currentUser.id}`; 
      console.log('ðŸŒ URL:', url);

      const token = localStorage.getItem('authToken');
      const response = await fetch(url, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestData)
      });

      console.log('ðŸ“¥ ×ª×’×•×‘×ª ×©×ž×™×¨×”:', response.status, response.statusText);

      if (response.ok) {
        const result = await response.json();
        console.log('âœ… ×ª×•×¦××ª ×©×ž×™×¨×”:', result);
