import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { GoogleLogin, GoogleOAuthProvider } from '@react-oauth/google';
import { useAuth } from '../contexts/AuthContext';
import { API_BASE_URL } from '../config';
import './LoginPage.css';

function LoginPage() {
  const [loginMessage, setLoginMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [googleUserData, setGoogleUserData] = useState(null);
  const { login } = useAuth();
  const navigate = useNavigate();
  const googleButtonRef = useRef(null);

  // Google OAuth Client ID
  const GOOGLE_CLIENT_ID = "386514389479-impprp7mgpalddmuflkvev582v8idjug.apps.googleusercontent.com";

  const handleGoogleLogin = async (credentialResponse) => {
    setIsLoading(true);
    setLoginMessage('转专 注 Google...');

    try {
      const response = await fetch(`${API_BASE_URL}/api/google-login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          credential: credentialResponse.credential
        })
      });

      const result = await response.json();

      if (result.success) {
        console.log('转专转 爪:', result.user);
        login(result.token, result.user);
        navigate('/dashboard');
      } else {
        if (result.isNewUser) {
          // 砖转砖 砖 - 砖专转 转 Google 注专 住 专砖
          console.log(' 砖转砖 砖 - 转 Google:', result.googleData);
          setGoogleUserData(result.googleData);
          setLoginMessage('砖转砖 砖 -  专砖 转');
          setTimeout(() => {
            console.log(' 注专 住 专砖 注 转:', result.googleData);
            navigate('/signup', { state: { googleData: result.googleData } });
          }, 1000);
        } else {
          setLoginMessage(result.message || '砖 转专转 注 Google');
        }
      }
    } catch (error) {
      console.error('砖 专 砖专转:', error);
      setLoginMessage('砖 专 砖专转. 住 砖.');
    } finally {
      setIsLoading(false);
    }
  };


  return (
    <GoogleOAuthProvider clientId={GOOGLE_CLIENT_ID}>
      <div className="login-page">
        <div className="logo-container">
          <img src="/logo1.png" alt="WOLFit Logo" className="login-logo" />
        </div>

        <div className="login-form">
          <h2 style={{ textAlign: 'center', marginBottom: '20px', color: '#333' }}>
          </h2>
          {/* 驻转专 Google OAuth 转 砖转 */}
          <div className="google-login-container">
            <div 
              className={`custom-google-button ${isLoading ? 'loading' : ''}`}
              onClick={!isLoading ? () => {
                // 专专 砖 驻转专 Google 住转专
                if (googleButtonRef.current) {
                  const googleButton = googleButtonRef.current.querySelector('div[role="button"]') ||
                                     googleButtonRef.current.querySelector('iframe');
                  if (googleButton) {
                    googleButton.click();
                  } else {
                    // 住 爪 转 驻转专 专 砖转
                    const hiddenButton = document.querySelector('iframe[src*="accounts.google.com"]') ||
                                       document.querySelector('div[role="button"]');
                    if (hiddenButton) {
                      hiddenButton.click();
                    } else {
                      console.log(' 爪 驻转专 Google');
                    }
                  }
                }
              } : undefined}
            >
              <div className="google-icon">
                <svg width="20" height="20" viewBox="0 0 24 24">
                  <path fill="#4285F4" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
                  <path fill="#34A853" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
                  <path fill="#FBBC05" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
                  <path fill="#EA4335" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
                </svg>
              </div>
              <span className="google-text">
                {isLoading ? '转专...' : '转专 注 Google'}
              </span>
              {isLoading && (
                <div className="loading-spinner"></div>
              )}
            </div>

            {/* 驻转专 Google 住转专 */}
            <div 
              ref={googleButtonRef}
              style={{ 
                position: 'absolute', 
                left: '-9999px', 
                opacity: 0, 
                pointerEvents: 'none',
                zIndex: -1
              }}
            >
              <GoogleLogin
                onSuccess={handleGoogleLogin}
                onError={() => setLoginMessage('砖 转专转 注 Google')}
                theme="outline"
                size="large"
                text="signin_with"
                shape="rectangular"
                logo_alignment="left"
                disabled={isLoading}
              />
            </div>
          </div>


          {/* 注 砖转砖 */}
          {loginMessage && (
            <p style={{ 
              color: loginMessage.includes('砖') ? '#000' : '#8b5cf6',
              textAlign: 'center',
              margin: '10px 0'
            }}>
              {loginMessage}
            </p>
          )}

          {/* 拽专 注 */}
          {isLoading && (
            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              justifyContent: 'center',
              marginTop: '10px'
            }}>
              <div style={{
                width: '16px',
                height: '16px',
                border: '2px solid #b38ed8',
                borderTop: '2px solid transparent',
                borderRadius: '50%',
                animation: 'spin 1s linear infinite',
                marginLeft: '8px'
              }}></div>
              转专 注 Google...
            </div>
          )}
        </div>
      </div>
    </GoogleOAuthProvider>
  );
}

export default LoginPage;
