import React, { useState, useEffect } from 'react';
import './CreateWorkout.css';

// ××™×¤×•×™ ××¡×¤×¨×™ ×”×¡×¤×•×¨×˜ ×œ×©××•×ª ×¢×‘×¨×™×™× (×ª×•×× ×œ×©×¨×ª)
const SPORT_MAPPING = {
  1: '×›×“×•×¨×’×œ',      // Soccer
  2: '×›×“×•×¨×¡×œ',      // Basketball  
  3: '×˜×™×¤×•×¡',       // Climbing
  4: '×—×“×¨ ×›×•×©×¨',    // Strength Training
  5: '×§×•×¨×“×™× ×¦×™×”',   // Coordination
  6: '×˜× ×™×¡',        // Tennis
  7: '×¤×™× ×’×¤×•× ×’',    // Ping Pong
  8: '×¨×™×§×•×“',       // Dance
  9: '××•×¤× ×™×™×'      // Cycling
};

function CreateWorkout({ user, selectedDate, startTime, endTime, onBackClick }) {
  const [isGenerating, setIsGenerating] = useState(false);
  const [workoutPlan, setWorkoutPlan] = useState(null);
  const [userPreferences, setUserPreferences] = useState([]);
  const [fieldsByTime, setFieldsByTime] = useState({});
  const [timeSlots, setTimeSlots] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [isSaving, setIsSaving] = useState(false);
  const [saveSuccess, setSaveSuccess] = useState(false);

  useEffect(() => {
    console.log('ğŸš€ CreateWorkout × ×˜×¢×Ÿ ×¢×:', {
      user: user?.userName,
      selectedDate,
      startTime,
      endTime
    });

    // ×‘×“×™×§×ª ×¤×•×¨××˜ ×”×ª××¨×™×š
    console.log('ğŸ“… ×¤×•×¨××˜ ×”×ª××¨×™×š:', {
      selectedDate,
      type: typeof selectedDate,
      length: selectedDate?.length
    });

    initializeWorkoutData();
  }, []);

  const initializeWorkoutData = async () => {
    try {
      setLoading(true);
      setError('');

      await loadUserPreferences();
      const slots = createTimeSlots();
      setTimeSlots(slots);
      await loadAvailableFields(slots);

      console.log('âœ… ×˜×¢×™× ×ª × ×ª×•× ×™× ×”×•×©×œ××”');

    } catch (err) {
      console.error('âŒ ×©×’×™××” ×‘×˜×¢×™× ×ª × ×ª×•× ×™×:', err);
      setError('×©×’×™××” ×‘×˜×¢×™× ×ª × ×ª×•× ×™×. ×× × × ×¡×” ×©×•×‘.');
    } finally {
      setLoading(false);
    }
  };

  const loadUserPreferences = async () => {
    try {
      if (!user || !user.id) {
        throw new Error('××©×ª××© ×œ× ××•×’×“×¨');
      }

      const url = `http://localhost:3001/api/user-preferences/${user.id}`;
      const response = await fetch(url);
      const data = await response.json();

      if (data.success) {
        setUserPreferences(data.data.selectedSports || []);
      } else {
        setUserPreferences([]);
      }
    } catch (error) {
      console.error('âŒ ×©×’×™××” ×‘×˜×¢×™× ×ª ×”×¢×“×¤×•×ª:', error);
      setUserPreferences([]);
      throw error;
    }
  };

  const createTimeSlots = () => {
    const slots = [];
    const [startHour, startMinute] = startTime.split(':').map(Number);
    const [endHour, endMinute] = endTime.split(':').map(Number);
    const startTotalMinutes = startHour * 60 + startMinute;
    const endTotalMinutes = endHour * 60 + endMinute;

    for (let minutes = startTotalMinutes; minutes < endTotalMinutes; minutes += 15) {
      const hours = Math.floor(minutes / 60);
      const mins = minutes % 60;
      const timeStr = `${hours.toString().padStart(2, '0')}:${mins.toString().padStart(2, '0')}`;
      slots.push(timeStr);
    }

    return slots;
  };

  const createTimeSlotsForRange = (start, end) => {
    const slots = [];
    const [startHour, startMinute] = start.split(':').map(Number);
    const [endHour, endMinute] = end.split(':').map(Number);
    const startTotalMinutes = startHour * 60 + startMinute;
    const endTotalMinutes = endHour * 60 + endMinute;

    for (let minutes = startTotalMinutes; minutes < endTotalMinutes; minutes += 15) {
      const hours = Math.floor(minutes / 60);
      const mins = minutes % 60;
      const timeStr = `${hours.toString().padStart(2, '0')}:${mins.toString().padStart(2, '0')}`;
      slots.push(timeStr);
    }

    return slots;
  };

  const loadAvailableFields = async (timeSlots) => {
    try {
      console.log('ğŸŸï¸ ×˜×•×¢×Ÿ ××’×¨×©×™× ×–××™× ×™×...');
      console.log('ğŸ“… ×ª××¨×™×š:', selectedDate);
      console.log('â° ×–×× ×™×:', timeSlots);

      const response = await fetch('http://localhost:3001/api/available-fields-for-workout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          date: selectedDate,
          timeSlots: timeSlots
        })
      });

      console.log('ğŸ“¥ ×ª×’×•×‘×” ××”×©×¨×ª:', response.status);

      if (response.ok) {
        const data = await response.json();
