import React, { useState, useEffect } from 'react';
import './SignUpScreen.css';

function SignUpScreen({ onBackToLogin, onSignUpComplete }) {
    const [userName, setUserName] = useState('');
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');
    const [height, setHeight] = useState('');
    const [weight, setWeight] = useState('');
    const [birthdate, setBirthdate] = useState('');
    const [showDatePicker, setShowDatePicker] = useState(false);
    const [selectedDay, setSelectedDay] = useState('');
    const [selectedMonth, setSelectedMonth] = useState('');
    const [selectedYear, setSelectedYear] = useState('');
    const [errors, setErrors] = useState({});

    const days = Array.from({length: 31}, (_, i) => i + 1);

    const months = [
        '专', '驻专专', '专抓', '驻专', '', '',
        '', '住', '住驻专', '拽专', '专', '爪专'
    ];

    const currentYear = new Date().getFullYear();
    const years = Array.from({length: 91}, (_, i) => currentYear - 10 - i);

    useEffect(() => {
        document.body.style.overflow = 'hidden';
        return () => {
          document.body.style.overflow = 'unset';
        };
    }, []);

    const validateField = (fieldName, value) => {
        let error = '';

        switch (fieldName) {
            case 'userName':
                if (!value.trim()) {
                    error = '砖 砖转砖  砖 ';
                } else if (value.length < 3) {
                    error = '砖 砖转砖   驻转 3 转';
                } else if (!/^[a-zA-Z0-9\s]+$/.test(value)) {
                    error = '砖 砖转砖   专拽 转转 转, 住驻专 专';
                }
                break;

            case 'password':
                if (!value) {
                    error = '住住  砖 ';
                } else if (value.length < 6) {
                    error = '住住 转  驻转 6 转';
                }
                 else if (!/^[a-zA-Z0-9\s]+$/.test(value)) {
                error = '砖 砖转砖   专拽 转转 转, 住驻专 专';
                }
                break;

            case 'email':
                if (!value) {
                    error = '  砖 ';
                } else if (!value.includes('@') || !value.includes('.')) {
                    error = '  转转  转拽';
                }

                break;

            case 'height':
                if (!value) {
                    error = '  砖 ';
                } else if (isNaN(value) || value < 100 || value > 250) {
                    error = '  转  100 -250 住"';
                }
                break;

            case 'weight':
                if (!value) {
                    error = '砖拽  砖 ';
                } else if (isNaN(value) || value < 30 || value > 300) {
                    error = '砖拽  转  30 -300 拽"';
                }
                break;

            case 'birthdate':
                if (!value) {
                    error = '转专   砖 ';
                } else if (!/^\d{2}\/\d{2}\/\d{4}$/.test(value)) {
                    error = '  转专 驻专 DD/MM/YYYY';
                }
                break;

            default:
                break;
        }

        return error;
    };

    const updateField = (fieldName, value, setter) => {
        setter(value);

        if (!value.trim()) {
            setErrors(prev => ({
                ...prev,
                [fieldName]: ''
            }));
            return;
        }

        const error = validateField(fieldName, value);
        console.log(`注 砖 ${fieldName}:`, { value, error });
        setErrors(prev => ({
            ...prev,
            [fieldName]: error
        }));
    };

    const isFormValid = () => {
        const allFieldsFilled = userName.trim() && password && email && height && weight && birthdate;

        console.log(' 砖转 :', allFieldsFilled);
        console.log('注专 砖转:', {
            userName: userName.trim(),
            password: password,
            email: email,
            height: height,
            weight: weight,
            birthdate: birthdate
        });

        return allFieldsFilled;
    };

    const handleContinue = () => {
        if (!isFormValid()) {
            return;
        }

        console.log('转:', {
            userName,
            password, 
            email,
            height,
            weight,
            birthdate
        });

        if (onSignUpComplete) {
          const userData = {
            userName,
            password, 
            email,
            height,
            weight,
            birthdate
            };
            onSignUpComplete(userData);
        }
    };

    return (
        <div className="signup-container">
          <button className="back-button" onClick={onBackToLogin}>
            专
          </button>

          <div className="content">
            <h1>专砖</h1>
            <p> 转 驻专 砖转砖 砖</p>

            <div className="signup-form">
              <div className="form-group">
                <input 
                  type="text" 
                  placeholder="砖 砖转砖"
                  value={userName}
                  onChange={(e) => updateField('userName', e.target.value, setUserName)}
                  onBlur={(e) => {
                    const error = validateField('userName', e.target.value);
                    setErrors(prev => ({
                      ...prev,
                      userName: error
                    }));
                  }}
                  className={errors.userName ? 'error' : ''}
                />
                {errors.userName && <div className="error-message">{errors.userName}</div>}
              </div>

              <div className="form-group">
                <input 
                  type="password" 
                  placeholder="住住"
                  value={password}
                  onChange={(e) => updateField('password', e.target.value, setPassword)}
                  onBlur={(e) => {
                    const error = validateField('password', e.target.value);
                    setErrors(prev => ({
                      ...prev,
                      password: error
                    }));
                  }}
                  className={errors.password ? 'error' : ''}
                />
                {errors.password && <div className="error-message">{errors.password}</div>}
              </div>

              <div className="form-group">
                <input 
                  type="email" 
                  placeholder=""
                  value={email}
                  onChange={(e) => updateField('email', e.target.value, setEmail)}
                  onBlur={(e) => {
                    const error = validateField('email', e.target.value);
                    setErrors(prev => ({
                      ...prev,
                      email: error
                    }));
                  }}
                  className={errors.email ? 'error' : ''}
                />
                {errors.email && <div className="error-message">{errors.email}</div>}
              </div>

              <div className="form-group">
                <input 
                  type="number" 
                  placeholder=" (住状)"
                  value={height}
                  onChange={(e) => updateField('height', e.target.value, setHeight)}
                  onBlur={(e) => {
                    const error = validateField('height', e.target.value);
                    setErrors(prev => ({
                      ...prev,
                      height: error
                    }));
                  }}
                  className={errors.height ? 'error' : ''}
                />
                {errors.height && <div className="error-message">{errors.height}</div>}
              </div>

              <div className="form-group">
                <input 
                  type="number" 
                  placeholder="砖拽 (拽状)"
                  value={weight}
                  onChange={(e) => updateField('weight', e.target.value, setWeight)}
                  onBlur={(e) => {
                    const error = validateField('weight', e.target.value);
                    setErrors(prev => ({
                      ...prev,
                      weight: error
                    }));
                  }}
                  className={errors.weight ? 'error' : ''}
                />
                {errors.weight && <div className="error-message">{errors.weight}</div>}
              </div>

              <div className="form-group">
                <div className="date-input-container">
                  <input 
                    type="text" 
                    placeholder="转专  (DD/MM/YYYY)"
                    value={birthdate}
                    onChange={(e) => {
                      let value = e.target.value;
                      value = value.replace(/[^\d/]/g, '');

                      if (value.length === 2 && !value.includes('/')) {
                        value = value + '/';
                      } else if (value.length === 5 && value.split('/').length === 2) {
                        value = value + '/';
                      }

                      //  -10 转 (DD/MM/YYYY)
                      if (value.length <= 10) {
                        updateField('birthdate', value, setBirthdate);
                      }
                    }}
                    onBlur={(e) => {
                      const error = validateField('birthdate', e.target.value);
                      setErrors(prev => ({
                        ...prev,
                        birthdate: error
                      }));
                    }}
                    maxLength="10"
                    className={errors.birthdate ? 'error' : ''}
                  />
                  <button 
                    type="button" 
                    className="date-picker-toggle"
                    onClick={() => setShowDatePicker(!showDatePicker)}
                  >

                  </button>
                </div>
                {errors.birthdate && <div className="error-message">{errors.birthdate}</div>}

                {showDatePicker && (
                  <div className="date-picker-dropdown">
                    <div className="date-selectors">
                      <select 
                        value={selectedDay} 
                        onChange={(e) => {
                          setSelectedDay(e.target.value);
                          if (selectedMonth && selectedYear) {
                            const monthIndex = months.indexOf(selectedMonth) + 1;
                            const newDate = `${e.target.value}/${monthIndex.toString().padStart(2, '0')}/${selectedYear}`;
                            updateField('birthdate', newDate, setBirthdate);
                          }
                        }}
                      >
                        <option value=""></option>
                        {days.map(day => (
                          <option key={day} value={day.toString().padStart(2, '0')}>{day}</option>
                        ))}
                      </select>

                      <select 
                        value={selectedMonth} 
                        onChange={(e) => {
                          setSelectedMonth(e.target.value);
                          if (selectedDay && selectedYear) {
                            const monthIndex = months.indexOf(e.target.value) + 1;
                            const newDate = `${selectedDay}/${monthIndex.toString().padStart(2, '0')}/${selectedYear}`;
                            updateField('birthdate', newDate, setBirthdate);
                          }
                        }}
                      >
                        <option value="">砖</option>
                        {months.map((month, index) => (
                          <option key={month} value={month}>{month}</option>
                        ))}
                      </select>

                      <select 
                        value={selectedYear} 
                        onChange={(e) => {
                          setSelectedYear(e.target.value);
                          if (selectedDay && selectedMonth) {
                            const monthIndex = months.indexOf(selectedMonth) + 1;
                            const newDate = `${selectedDay}/${monthIndex.toString().padStart(2, '0')}/${e.target.value}`;
                            updateField('birthdate', newDate, setBirthdate);
                          }
                        }}
                      >
                        <option value="">砖</option>
                        {years.map(year => (
                          <option key={year} value={year}>{year}</option>
                        ))}
                      </select>
                    </div>
                  </div>
                )}
              </div>

              {(() => {
                const isValid = isFormValid();
                console.log('爪 驻转专:', isValid);
                return isValid ? (
                  <button 
                    className="signup-button"
                    onClick={handleContinue}
                  >
                    砖
                  </button>
                ) : (
                  <div className="form-statu">

                  </div>
                );
              })()}
            </div>
          </div>
        </div>
  );
}

export default SignUpScreen;
