                  }}
                >
                  <option value="">◊ë◊ó◊® ◊©◊¢◊î</option>
                  {generateTimeOptions().map(time => (
                    <option key={time} value={time}>{time}</option>
                  ))}
                </select>
                {blockedTimes.length > 0 && (
                  <div style={{
                    marginTop: '10px',
