import React, { useState } from 'react';
import './App.css';
import SignUpScreen from './SignUpScreen.js';
import SignUpPreferences from './SignUpPreferences.js';
import WelcomeScreen from './WelcomeScreen.js';
import { GoogleLogin, GoogleOAuthProvider } from '@react-oauth/google';
import './mobile-fix.css';
import { API_BASE_URL } from './config';
function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentScreen, setCurrentScreen] = useState('login');
  const [userBasicData, setUserBasicData] = useState(null);
  const [loginMessage, setLoginMessage] = useState(''); 
  const [loggedInUser, setLoggedInUser] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [googleUserData, setGoogleUserData] = useState(null);


  // Google OAuth Client ID
  const GOOGLE_CLIENT_ID = "386514389479-impprp7mgpalddmuflkvev582v8idjug.apps.googleusercontent.com"; 

  const handleGoogleLogin = async (credentialResponse) => {
    setIsLoading(true);
    setLoginMessage('转专 注 Google...');

    try {
      const response = await fetch(`${API_BASE_URL}/api/google-login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          credential: credentialResponse.credential
        })
      });

      const result = await response.json();

      if (result.success) {
        console.log('转专转 爪:', result.user);
        setLoggedInUser(result.user);
        setIsLoggedIn(true);
        setLoginMessage('');
      } else {
        if (result.isNewUser) {
          // 砖转砖 砖 - 砖专转 转 Google 注专 住 专砖
          setGoogleUserData(result.googleData);
          setLoginMessage('砖转砖 砖 -  专砖 转');
          setTimeout(() => {
            handleGoToSignUp();
          }, 1000);
        } else {
          setLoginMessage(result.message || '砖 转专转 注 Google');
        }
      }
    } catch (error) {
      console.error('砖 专 砖专转:', error);
      setLoginMessage('砖 专 砖专转. 住 砖.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleGoToSignUp = () => {
    console.log('注专 住 专砖');
    setCurrentScreen('signup');
  };

  const handleBackToLogin = () => {
    console.log('专 住 转专转');
    setCurrentScreen('login');
    setUserBasicData(null);
    setUserName('');
    setPassword('');
    setLoginMessage('');
  };

  const handleSignUpContinue = (basicData) => {
    console.log('砖 转 住住:', basicData);
    setUserBasicData(basicData); 
    setCurrentScreen('signupPreferences');
  };

  const handleCompleteSignUp = async (completeUserData) => {
    console.log('专砖 砖 爪!');
    console.log('转 砖转砖 :', completeUserData);

    try {
      // 住驻转 转 Google 转 专砖
      const registrationData = {
        userName: googleUserData?.name || completeUserData.userName,
        email: googleUserData?.email || completeUserData.email,
        password: completeUserData.password || '',
        height: completeUserData.height,
        weight: completeUserData.weight,
        birthdate: completeUserData.birthdate,
        intensityLevel: completeUserData.intensityLevel,
        googleId: googleUserData?.googleId,
        profilePicture: googleUserData?.picture,
        authProvider: 'google',
        sportPreferences: completeUserData.selectedSports || []
      };

      console.log('转 专砖:', registrationData);

      const response = await fetch(`${API_BASE_URL}/api/register`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(registrationData)
      });

      const result = await response.json();

      if (result.success) {
        console.log('专砖 砖 爪:', result);
        setLoggedInUser(result.user);
        setIsLoggedIn(true);
        setCurrentScreen('login');
        setGoogleUserData(null); // 拽 转 Google
      } else {
        alert('砖 专砖: ' + result.message);
      }

    } catch (error) {
      console.error('砖 专砖:', error);
      alert('砖 专 砖专转');
    }
  };

  const handleBackToSignUp = () => {
    console.log('专 住 专砖');
    setCurrentScreen('signup');
  };

  if (isLoggedIn) {
    return <WelcomeScreen user={loggedInUser} />; 
  }

  if (currentScreen === 'signup') {
    return (
      <SignUpScreen 
        onBackToLogin={handleBackToLogin}
        onSignUpComplete={handleSignUpContinue}
      />
    );
  }

  if (currentScreen === 'signupPreferences') {
    return (
      <SignUpPreferences 
        onBackClick={handleBackToSignUp}
        onCompleteSignUp={handleCompleteSignUp}
        userBasicData={userBasicData}
      />
    );
  }

  //  住 转专转 砖 注 State
  return (
    <GoogleOAuthProvider clientId={GOOGLE_CLIENT_ID}>
      <div className="App">
        <div className="logo-container">
          <img src="/logo1.png" alt="WOLFit Logo" className="login-logo" />
        </div>

        <div className="login-form">
          {/* 驻转专 Google OAuth */}
          <div style={{ 
            display: 'flex', 
            justifyContent: 'center', 
            marginBottom: '20px',
            opacity: isLoading ? 0.6 : 1
          }}>
            <GoogleLogin
              onSuccess={handleGoogleLogin}
              onError={() => setLoginMessage('砖 转专转 注 Google')}
              theme="outline"
              size="large"
              text="signin_with"
              shape="rectangular"
              logo_alignment="left"
              disabled={isLoading}
            />
          </div>

        {/* 注 砖转砖 */}
        {loginMessage && (
          <p style={{ 
            color: loginMessage.includes('砖') ? '#000' : '#8b5cf6',
            textAlign: 'center',
            margin: '10px 0'
          }}>
            {loginMessage}
          </p>
        )}

        {/* 拽专 注 */}
        {isLoading && (
          <div style={{ 
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'center',
            marginTop: '10px'
          }}>
            <div style={{
              width: '16px',
              height: '16px',
              border: '2px solid #b38ed8',
              borderTop: '2px solid transparent',
              borderRadius: '50%',
              animation: 'spin 1s linear infinite',
              marginLeft: '8px'
            }}></div>
            转专 注 Google...
          </div>
        )}


      </div>
    </div>
    </GoogleOAuthProvider>
  );
}

export default App;
