import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from './contexts/AuthContext';
import './OrderTrain.css';
import './EditUser.css';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import CreateWorkout from './CreateWorkout';
import { API_BASE_URL } from './config';

function OrderTrain(){
  const { user } = useAuth();
  const navigate = useNavigate();
  const [selectDate, setSelectDate] = useState(null);
  const [startTime, setStartTime] = useState(null);
  const [endTime, setEndTime] = useState(null);
  const [showCreateWorkout, setShowCreateWorkout] = useState(false);
  const [blockedTimes, setBlockedTimes] = useState([]);
  const [loadingBlockedTimes, setLoadingBlockedTimes] = useState(false);
  const [availableTimes, setAvailableTimes] = useState([]);

  // üöÄ ◊™◊ô◊ß◊ï◊ü 1: ◊î◊ï◊°◊§◊™ debouncing ◊ï-cache
  const [debounceTimer, setDebounceTimer] = useState(null);
  const [blockedTimesCache, setBlockedTimesCache] = useState({});

  useEffect(() => {
    // ◊ú◊ê ◊†◊ó◊°◊ï◊ù ◊ê◊™ ◊î◊í◊ú◊ô◊ú◊î ◊ë◊û◊°◊ö ◊î◊ñ◊û◊†◊™ ◊ê◊ô◊û◊ï◊ü
    return () => {
      // ◊®◊ß ◊†◊ó◊ñ◊ô◊® ◊ê◊™ ◊î◊í◊ú◊ô◊ú◊î ◊õ◊©◊¢◊ï◊ñ◊ë◊ô◊ù
    };
  }, []);

  // üöÄ ◊™◊ô◊ß◊ï◊ü 2: debouncing - ◊û◊†◊ô◊¢◊™ ◊ß◊®◊ô◊ê◊ï◊™ ◊û◊®◊ï◊ë◊ï◊™ ◊û◊î◊ô◊®◊ï◊™
  useEffect(() => {
    if (selectDate && user?.id) {
      // ◊ë◊ô◊ò◊ï◊ú ◊ò◊ô◊ô◊û◊® ◊ß◊ï◊ì◊ù ◊ê◊ù ◊ß◊ô◊ô◊ù
      if (debounceTimer) {
        clearTimeout(debounceTimer);
      }

      // ◊ô◊¶◊ô◊®◊™ ◊ò◊ô◊ô◊û◊® ◊ó◊ì◊© - ◊î◊û◊™◊ü 500ms ◊ú◊§◊†◊ô ◊î◊ß◊®◊ô◊ê◊î
      const timer = setTimeout(() => {
        loadBlockedTimes();
      }, 500);

      setDebounceTimer(timer);
    } else {
      setBlockedTimes([]);
    }

    // ◊†◊ô◊ß◊ï◊ô ◊ò◊ô◊ô◊û◊® ◊õ◊©◊î◊ß◊ï◊û◊§◊ï◊†◊†◊ò◊î ◊†◊î◊®◊°◊™
    return () => {
      if (debounceTimer) {
        clearTimeout(debounceTimer);
      }
    };
  }, [selectDate, user?.id]);

  // ◊¢◊ì◊õ◊ï◊ü ◊©◊¢◊ï◊™ ◊ñ◊û◊ô◊†◊ï◊™ ◊õ◊©◊î◊©◊¢◊ï◊™ ◊î◊™◊§◊ï◊°◊ï◊™ ◊û◊©◊™◊†◊ï◊™
  useEffect(() => {
    if (selectDate) {
      const times = generateTimeOptions();
      setAvailableTimes(times);
      console.log('üîÑ ◊¢◊ì◊õ◊†◊™◊ô ◊©◊¢◊ï◊™ ◊ñ◊û◊ô◊†◊ï◊™:', times);
    }
  }, [blockedTimes, selectDate]);

  // üöÄ ◊™◊ô◊ß◊ï◊ü 3: ◊î◊ï◊°◊§◊™ cache ◊ú◊û◊†◊ô◊¢◊™ ◊ß◊®◊ô◊ê◊ï◊™ ◊û◊ô◊ï◊™◊®◊ï◊™
  const loadBlockedTimes = async () => {
    try {
      const dateStr = selectDate.toLocaleDateString('en-CA'); // YYYY-MM-DD

      // ◊ë◊ì◊ô◊ß◊î ◊ê◊ù ◊ô◊© cache ◊ú◊™◊ê◊®◊ô◊ö ◊î◊ñ◊î
      if (blockedTimesCache[dateStr]) {
        console.log('üì¶ ◊û◊©◊™◊û◊© ◊ë-cache ◊ú◊©◊¢◊ï◊™ ◊™◊§◊ï◊°◊ï◊™ ◊¢◊ë◊ï◊®', dateStr);
        setBlockedTimes(blockedTimesCache[dateStr]);
        return;
      }

      setLoadingBlockedTimes(true);
      const url = `${API_BASE_URL}/api/user-booked-times/${user.id}/${dateStr}`;

      console.log('üîç ◊ò◊ï◊¢◊ü ◊©◊¢◊ï◊™ ◊™◊§◊ï◊°◊ï◊™ ◊û:', url);

      const token = localStorage.getItem('authToken');
      console.log('üîë ◊ò◊ï◊ß◊ü:', token ? '◊ß◊ô◊ô◊ù' : '◊ó◊°◊®');

      const response = await fetch(url, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      console.log('üì° ◊™◊í◊ï◊ë◊™ ◊î◊©◊®◊™:', response.status, response.statusText);
      const data = await response.json();

      if (data.success) {
        setBlockedTimes(data.blockedTimes);

        // üöÄ ◊©◊û◊ô◊®◊î ◊ë-cache ◊ú◊û◊†◊ô◊¢◊™ ◊ß◊®◊ô◊ê◊ï◊™ ◊ó◊ï◊ñ◊®◊ï◊™
        setBlockedTimesCache(prev => ({
          ...prev,
          [dateStr]: data.blockedTimes
        }));

        console.log('üö´ ◊©◊¢◊ï◊™ ◊™◊§◊ï◊°◊ï◊™ ◊†◊ò◊¢◊†◊ï:', data.blockedTimes);
        console.log('üìä ◊û◊°◊§◊® ◊©◊¢◊ï◊™ ◊™◊§◊ï◊°◊ï◊™:', data.blockedTimes.length);
      } else {
        console.log('‚ö†Ô∏è ◊©◊í◊ô◊ê◊î ◊ë◊ò◊¢◊ô◊†◊™ ◊©◊¢◊ï◊™ ◊™◊§◊ï◊°◊ï◊™:', data.message);
        setBlockedTimes([]);
      }
    } catch (error) {
      console.error('‚ùå ◊©◊í◊ô◊ê◊î ◊ë◊ò◊¢◊ô◊†◊™ ◊©◊¢◊ï◊™ ◊™◊§◊ï◊°◊ï◊™:', error);
      setBlockedTimes([]);
    } finally {
      setLoadingBlockedTimes(false);
    }
  };

  function isDateAllowed(date){
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    const sevenDaysFromNow = new Date();
    sevenDaysFromNow.setDate(today.getDate() + 7);
    sevenDaysFromNow.setHours(23, 59, 59, 999);

    const dateToCheck = new Date(date);
    dateToCheck.setHours(0, 0, 0, 0);

    return dateToCheck >= today && dateToCheck <= sevenDaysFromNow;
  }

  // üöÄ ◊™◊ô◊ß◊ï◊ü 4: ◊î◊§◊ó◊™◊™ ◊ú◊ï◊í◊ô◊ù - ◊î◊°◊®◊™ ◊î◊ï◊ì◊¢◊ï◊™ ◊û◊ô◊ï◊™◊®◊ï◊™
  function isTimeBlocked(timeString) {
    // ◊ë◊ì◊ô◊ß◊î ◊ê◊ù ◊î◊©◊¢◊ï◊™ ◊î◊™◊§◊ï◊°◊ï◊™ ◊†◊ò◊¢◊†◊ï ◊ë◊õ◊ú◊ú
    if (!blockedTimes || blockedTimes.length === 0) {
      // ◊î◊°◊®◊™ ◊î◊ú◊ï◊í ◊î◊ñ◊î - ◊î◊ï◊ê ◊ô◊ï◊¶◊® ◊®◊¢◊©!
      // console.log(`‚ö†Ô∏è ◊ê◊ô◊ü ◊©◊¢◊ï◊™ ◊™◊§◊ï◊°◊ï◊™ ◊†◊ò◊¢◊†◊ï◊™, ◊©◊¢◊î ${timeString} ◊ñ◊û◊ô◊†◊î`);
      return false;
    }

    // ◊î◊°◊®◊™ ◊ú◊ï◊í◊ô◊ù ◊û◊ô◊ï◊™◊®◊ô◊ù - ◊®◊ß ◊ë◊ì◊ô◊ß◊î ◊©◊ß◊ò◊î
    // console.log(`üîç ◊ë◊ï◊ì◊ß ◊ê◊ù ◊©◊¢◊î ${timeString} ◊ó◊°◊ï◊û◊î`);
    // console.log(`üìã ◊©◊¢◊ï◊™ ◊™◊§◊ï◊°◊ï◊™ ◊û◊î◊©◊®◊™:`, blockedTimes);

    // ◊î◊©◊®◊™ ◊õ◊ë◊® ◊û◊ó◊ñ◊ô◊® ◊ê◊™ ◊õ◊ú ◊î◊©◊¢◊ï◊™ ◊î◊ó◊°◊ï◊û◊ï◊™ ◊õ◊ï◊ú◊ú ◊®◊ë◊¢ ◊©◊¢◊î ◊ú◊§◊†◊ô ◊ï◊ê◊ó◊®◊ô
    const isBlocked = blockedTimes.includes(timeString);

    // ◊î◊°◊®◊™ ◊ú◊ï◊í◊ô◊ù ◊û◊ô◊ï◊™◊®◊ô◊ù
    // if (isBlocked) {
    //   console.log(`‚ùå ◊©◊¢◊î ${timeString} ◊ó◊°◊ï◊û◊î`);
    // } else {
    //   console.log(`‚úÖ ◊©◊¢◊î ${timeString} ◊ñ◊û◊ô◊†◊î`);
    // }

    return isBlocked;
  }

  // ◊§◊ï◊†◊ß◊¶◊ô◊î ◊ú◊ë◊ì◊ô◊ß◊î ◊ê◊ù ◊ò◊ï◊ï◊ó ◊ñ◊û◊ü ◊ó◊ï◊§◊£ ◊ú◊ê◊ô◊û◊ï◊ü ◊ß◊ô◊ô◊ù
  function isTimeRangeBlocked(startTime, endTime) {
    if (!startTime || !endTime || !blockedTimes || blockedTimes.length === 0) {
      return false;
    }

    console.log(`üîç ◊ë◊ï◊ì◊ß ◊ê◊ù ◊ò◊ï◊ï◊ó ${startTime}-${endTime} ◊ó◊ï◊§◊£ ◊ú◊ê◊ô◊û◊ï◊ü ◊ß◊ô◊ô◊ù`);
    console.log(`üìã ◊©◊¢◊ï◊™ ◊™◊§◊ï◊°◊ï◊™:`, blockedTimes);

    // ◊î◊û◊®◊î ◊©◊ú ◊î◊ñ◊û◊†◊ô◊ù ◊ú◊ì◊ß◊ï◊™
    const [startHour, startMinute] = startTime.split(':').map(Number);
    const [endHour, endMinute] = endTime.split(':').map(Number);
    const startMinutes = startHour * 60 + startMinute;
    const endMinutes = endHour * 60 + endMinute;

    // ◊ë◊ì◊ô◊ß◊î ◊ê◊ù ◊ô◊© ◊ê◊ô◊û◊ï◊ü ◊ë◊ò◊ï◊ï◊ó
    for (const blockedTime of blockedTimes) {
      const [blockedHour, blockedMinute] = blockedTime.split(':').map(Number);
      const blockedMinutes = blockedHour * 60 + blockedMinute;

      // ◊ë◊ì◊ô◊ß◊î ◊ê◊ù ◊î◊ê◊ô◊û◊ï◊ü ◊î◊™◊§◊ï◊° ◊†◊û◊¶◊ê ◊ë◊ò◊ï◊ï◊ó ◊©◊†◊ë◊ó◊®
      if (blockedMinutes >= startMinutes && blockedMinutes < endMinutes) {
        console.log(`‚ùå ◊ò◊ï◊ï◊ó ${startTime}-${endTime} ◊ó◊ï◊§◊£ ◊ú◊ê◊ô◊û◊ï◊ü ◊ë-${blockedTime}`);
        return true;
      }
    }

    console.log(`‚úÖ ◊ò◊ï◊ï◊ó ${startTime}-${endTime} ◊ú◊ê ◊ó◊ï◊§◊£ ◊ú◊ê◊ô◊û◊ï◊ü ◊ß◊ô◊ô◊ù`);
    return false;
  }

  // üöÄ ◊™◊ô◊ß◊ï◊ü 5: ◊î◊§◊ó◊™◊™ ◊ú◊ï◊í◊ô◊ù ◊ë-generateTimeOptions
  function generateTimeOptions() {
    // ◊î◊°◊®◊™ ◊ú◊ï◊í◊ô◊ù ◊û◊ô◊ï◊™◊®◊ô◊ù - ◊®◊ß ◊ú◊ï◊í ◊ê◊ó◊ì ◊ë◊°◊ï◊£
    // console.log('üöÄ ◊û◊™◊ó◊ô◊ú ◊ú◊ô◊¶◊ï◊® ◊ê◊§◊©◊®◊ï◊ô◊ï◊™ ◊ñ◊û◊ü');
    // console.log('üìÖ ◊™◊ê◊®◊ô◊ö ◊†◊ë◊ó◊®:', selectDate);
    // console.log('üö´ ◊©◊¢◊ï◊™ ◊™◊§◊ï◊°◊ï◊™:', blockedTimes);

    const times = [];
    const now = new Date();
    const isToday = selectDate && 
      selectDate.getDate() === now.getDate() &&
      selectDate.getMonth() === now.getMonth() &&
      selectDate.getFullYear() === now.getFullYear();

    for (let hour = 6; hour <= 23; hour++) {
      for (let minute = 0; minute < 60; minute += 15) {
        if (hour === 23 && minute > 0) break;

        if (isToday) {
          const currentHour = now.getHours();
          const currentMinute = now.getMinutes();
          const timeInMinutes = hour * 60 + minute;
          const nowInMinutes = currentHour * 60 + currentMinute + 30;

          if (timeInMinutes < nowInMinutes) {
            continue;
          }
        }

        const timeString = `${hour.toString().padStart(2, '0')}:${minute.toString().padStart(2, '0')}`;

        // ◊ë◊ì◊ô◊ß◊î ◊ê◊ù ◊î◊©◊¢◊î ◊™◊§◊ï◊°◊î (◊õ◊ï◊ú◊ú ◊®◊ë◊¢ ◊©◊¢◊î ◊ú◊§◊†◊ô ◊ï◊ê◊ó◊®◊ô)
        if (!isTimeBlocked(timeString)) {
          times.push(timeString);
        } else {
          // ◊î◊°◊®◊™ ◊ú◊ï◊í ◊û◊ô◊ï◊™◊®
          // console.log(`üö´ ◊©◊¢◊î ${timeString} ◊†◊ó◊°◊û◊î ◊ï◊ú◊ê ◊†◊ï◊°◊§◊î ◊ú◊®◊©◊ô◊û◊î`);
        }
      }
    }

    // ◊®◊ß ◊ú◊ï◊í ◊ê◊ó◊ì ◊ë◊°◊ï◊£ ◊ë◊û◊ß◊ï◊ù 49 ◊ú◊ï◊í◊ô◊ù
    console.log(`üìã ◊†◊ï◊¶◊®◊ï ${times.length} ◊©◊¢◊ï◊™ ◊ñ◊û◊ô◊†◊ï◊™`);
    return times;
  }

  function generateEndTimeOptions() {
    if (!startTime) return [];

    const [startHour, startMinute] = startTime.split(':').map(Number);
    const startTimeInMinutes = startHour * 60 + startMinute;
    const times = [];

    for (let duration = 30; duration <= 90; duration += 15) {
      const endTimeInMinutes = startTimeInMinutes + duration;
      const endHour = Math.floor(endTimeInMinutes / 60);
      const endMinute = endTimeInMinutes % 60;

      if (endHour <= 23 && !(endHour === 23 && endMinute > 30)) {
        const timeString = `${endHour.toString().padStart(2, '0')}:${endMinute.toString().padStart(2, '0')}`;

