import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './SignUpScreen.css';

function SignUpScreen() {
  const navigate = useNavigate();
  const location = useLocation();
  const googleData = location.state?.googleData;
    const [userName, setUserName] = useState(googleData?.name || '');
    const [email, setEmail] = useState(googleData?.email || '');
    const [height, setHeight] = useState('');
    const [weight, setWeight] = useState('');
    const [birthdate, setBirthdate] = useState('');
    const [errors, setErrors] = useState({});
    const [touched, setTouched] = useState({});


    useEffect(() => {
        document.body.style.overflow = 'hidden';
        return () => {
          document.body.style.overflow = 'unset';
        };
    }, []);

    //  拽
    useEffect(() => {
        console.log(' SignUpScreen - 转 Google:', googleData);
        console.log(' SignUpScreen - 砖 砖转砖:', userName);
        console.log(' SignUpScreen - :', email);
    }, [googleData, userName, email]);

    const validateField = (fieldName, value) => {
        let error = '';

        switch (fieldName) {
            case 'userName':
                if (value && value.trim().length < 3) {
                    error = '砖 砖转砖   驻转 3 转';
                }
                break;

            case 'email':
                if (value && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value)) {
                    error = '  转拽';
                }
                break;

            case 'height':
                if (value && (isNaN(value) || value < 100 || value > 250)) {
                    error = '  转  100 -250 住"';
                }
                break;

            case 'weight':
                if (value && (isNaN(value) || value < 30 || value > 300)) {
                    error = '砖拽  转  30 -300 拽"';
                }
                break;

            case 'birthdate':
                if (value && !/^\d{2}\/\d{2}\/\d{4}$/.test(value)) {
                    error = '  转专 驻专 DD/MM/YYYY';
                }
                break;

            default:
                break;
        }

        return error;
    };

    const updateField = (fieldName, value, setter) => {
        setter(value);

        // 拽 砖  砖 专拽
        if (!value.trim()) {
            setErrors(prev => ({
                ...prev,
                [fieldName]: ''
            }));
        }
    };

    const validateFieldOnBlur = (fieldName, value) => {
        // 住 砖砖 注 
        setTouched(prev => ({
            ...prev,
            [fieldName]: true
        }));

        if (!value.trim()) {
            setErrors(prev => ({
                ...prev,
                [fieldName]: ''
            }));
            return;
        }

        const error = validateField(fieldName, value);
        setErrors(prev => ({
            ...prev,
            [fieldName]: error
        }));
    };

    // 拽  砖 砖转 驻住
    const hasErrors = () => {
        // 拽转 砖转 
        if (!userName.trim() || !email.trim()) {
            return true;
        }

        // 拽转 砖转 爪
        return Object.values(errors).some(error => error !== '');
    };


    const handleContinue = () => {
        // 拽转 砖转 
        if (!userName.trim()) {
            setErrors({ userName: '砖 砖转砖 专砖' });
            return;
        }
        if (!email.trim()) {
            setErrors({ email: ' 专砖' });
            return;
        }

        // 拽转 砖转 驻爪   
        const newErrors = {};

        if (height.trim()) {
            const heightError = validateField('height', height);
            if (heightError) newErrors.height = heightError;
        }

        if (weight.trim()) {
            const weightError = validateField('weight', weight);
            if (weightError) newErrors.weight = weightError;
        }

        if (birthdate.trim()) {
            const birthdateError = validateField('birthdate', birthdate);
            if (birthdateError) newErrors.birthdate = birthdateError;
        }

        //  砖 砖转, 爪 转 注爪专
        if (Object.keys(newErrors).length > 0) {
            setErrors(prev => ({ ...prev, ...newErrors }));
            return;
        }

        console.log('转:', {
            userName,
            email,
            height,
            weight,
            birthdate
        });

        const userData = {
          userName,
          email,
          height,
          weight,
          birthdate
        };
        navigate('/signup-preferences', { state: { userData, googleData } });
    };

    return (
        <div className="signup-container">
          <button className="back-button" onClick={() => navigate('/login')}>
            专
          </button>

          <div className="content">
            <h1>砖转 专砖</h1>
            {googleData ? (
              <div>
                <p style={{ color: '#8b5cf6', fontWeight: 'bold', marginBottom: '10px' }}>
                   专 ! 转专转 注 Google
                </p>
                <p>转 Google 砖 注 转.  砖 转 驻专 :</p>
              </div>
            ) : (
              <p>注 住! 专拽  驻专 住驻 (驻爪)</p>
            )}

            <div className="signup-form">
              <div className="form-group">
                <input 
                  type="text" 
                  placeholder="砖 砖转砖"
                  value={userName}
                  onChange={(e) => updateField('userName', e.target.value, setUserName)}
                  onBlur={(e) => validateFieldOnBlur('userName', e.target.value)}
                  className={errors.userName ? 'error' : ''}
                  required
                />
                {errors.userName && <span className="error-message">{errors.userName}</span>}
              </div>

              <div className="form-group">
                <input 
                  type="email" 
                  placeholder=""
                  value={email}
                  onChange={(e) => updateField('email', e.target.value, setEmail)}
                  onBlur={(e) => validateFieldOnBlur('email', e.target.value)}
                  className={errors.email ? 'error' : ''}
                  required
                />
                {errors.email && <span className="error-message">{errors.email}</span>}
              </div>

              <div className="form-group">
                <input 
                  type="number" 
                  placeholder=" (住状) - 驻爪"
                  value={height}
                  onChange={(e) => updateField('height', e.target.value, setHeight)}
                  onBlur={(e) => validateFieldOnBlur('height', e.target.value)}
                  className={errors.height ? 'error' : ''}
                />
                {errors.height && <span className="error-message">{errors.height}</span>}
              </div>

              <div className="form-group">
                <input 
                  type="number" 
                  placeholder="砖拽 (拽状) - 驻爪"
                  value={weight}
                  onChange={(e) => updateField('weight', e.target.value, setWeight)}
                  onBlur={(e) => validateFieldOnBlur('weight', e.target.value)}
                  className={errors.weight ? 'error' : ''}
                />
                {errors.weight && <span className="error-message">{errors.weight}</span>}
              </div>

              <div className="form-group">
                <input 
                  type="date" 
                  placeholder="转专  - 驻爪"
                  value={birthdate}
                  onChange={(e) => {
                    const dateValue = e.target.value;
                    if (dateValue) {
                      // 专转 -YYYY-MM-DD -DD/MM/YYYY
                      const [year, month, day] = dateValue.split('-');
                      const formattedDate = `${day}/${month}/${year}`;
                      setBirthdate(formattedDate);
                      updateField('birthdate', formattedDate, setBirthdate);
                    } else {
                      setBirthdate('');
                      updateField('birthdate', '', setBirthdate);
                    }
                  }}
                  onBlur={(e) => {
                    const dateValue = e.target.value;
                    if (dateValue) {
                      const [year, month, day] = dateValue.split('-');
                      const formattedDate = `${day}/${month}/${year}`;
                      validateFieldOnBlur('birthdate', formattedDate);
                    } else {
                      validateFieldOnBlur('birthdate', '');
                    }
                  }}
                  className={errors.birthdate ? 'error' : ''}
                />
                {errors.birthdate && <span className="error-message">{errors.birthdate}</span>}
              </div>

              <button 
                className="continue-button"
                onClick={handleContinue}
                disabled={hasErrors()}
              >
                砖
              </button>
            </div>
          </div>
        </div>
    );
}

export default SignUpScreen;
