import React, { useState, useEffect } from 'react';
import { Navigate } from 'react-router-dom';
import { API_BASE_URL } from '../config';

function ProtectedRoute({ children }) {
  const [isAuthenticated, setIsAuthenticated] = useState(null); // null = loading, true = authenticated, false = not authenticated
  const [user, setUser] = useState(null);

  useEffect(() => {
    const checkAuth = async () => {
      const token = localStorage.getItem('authToken');

      if (!token) {
        setIsAuthenticated(false);
        return;
      }

      try {
        const response = await fetch(`${API_BASE_URL}/api/verify-token`, {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        });

        const result = await response.json();

        if (result.success) {
          setUser(result.user);
          setIsAuthenticated(true);
        } else {
          // טוקן לא תקין - נמחק אותו
          localStorage.removeItem('authToken');
          setIsAuthenticated(false);
        }
      } catch (error) {
        console.error('שגיאה בבדיקת טוקן:', error);
        localStorage.removeItem('authToken');
        setIsAuthenticated(false);
      }
    };

    checkAuth();
  }, []);

  // עדיין טוען
  if (isAuthenticated === null) {
    return (
      <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        fontSize: '18px',
        color: '#8b5cf6'
      }}>
        <div style={{
          width: '40px',
          height: '40px',
          border: '4px solid #b38ed8',
          borderTop: '4px solid transparent',
          borderRadius: '50%',
          animation: 'spin 1s linear infinite',
          marginLeft: '10px'
        }}></div>
        בודק הרשאות...
      </div>
    );
  }

  // לא מחובר - מעבר למסך התחברות
  if (isAuthenticated === false) {
    return <Navigate to="/login" replace />;
  }

  // מחובר - הצגת התוכן המוגן
  return React.cloneElement(children, { user });
}

export default ProtectedRoute;
